db.courses.insert({'name': 'Introduction to Computer Science and Programming', 'tags': ['REST'], 'title': '6.00 Introduction to Computer Science and Programming', 'total_units': 12, 'course_numbers': ['6.00'], 'units': '3-7-2', 'description': 'Introduction to computer science and programming for students with little or no programming experience. Students learn how to program and how to use computational techniques to solve problems. Topics include software design, algorithms, data analysis, and simulation techniques. Assignments are done using the Python programming language. Meets with 6.0001 first half of term and 6.0002 second half of term. Credit cannot also be received for 6.0001 or 6.0002. Final given during final exam week.'});
db.courses.insert({'description': 'Introduction to computer science and programming for students with little or no programming experience. Students develop skills to program and use computational techniques to solve problems. Topics include the notion of computation, Python, simple algorithms and data structures, testing and debugging, and algorithmic complexity. Combination of 6.0001 and 6.0002 counts as REST subject. Final given in the seventh week of the term.', 'title': '6.0001 Introduction to Computer Science Programming in Python', 'total_units': 6, 'course_numbers': ['6.0001'], 'units': '2-3-1', 'name': 'Introduction to Computer Science Programming in Python'});
db.courses.insert({'description': 'Provides an introduction to using computation to understand real-world phenomena. Topics include plotting, stochastic programs, probability and statistics, random walks, Monte Carlo simulations, modeling data, optimization problems, and clustering. Combination of 6.0001 and 6.0002 counts as REST subject. Final given during final exam week.', 'title': '6.0002 Introduction to Computational Thinking and Data Science', 'total_units': 6, 'course_numbers': ['6.0002'], 'units': '2-3-1', 'name': 'Introduction to Computational Thinking and Data Science'});
db.courses.insert({'name': 'Circuits and Electronics', 'tags': ['REST'], 'title': '6.002 Circuits and Electronics', 'total_units': 12, 'course_numbers': ['6.002'], 'units': '4-1-7', 'description': 'Fundamentals of the lumped circuit abstraction. Resistive elements and networks, independent and dependent sources, switches and MOS devices, digital abstraction, amplifiers, and energy storage elements. Dynamics of first- and second-order networks; design in the time and frequency domains; analog and digital circuits and applications. Design exercises. Occasional laboratory.'});
db.courses.insert({'name': 'Signals and Systems', 'tags': ['REST'], 'title': '6.003 Signals and Systems', 'total_units': 12, 'course_numbers': ['6.003'], 'units': '5-0-7', 'description': 'Presents the fundamentals of signal and system analysis. Topics include discrete-time and continuous-time signals, Fourier series and transforms, Laplace and Z transforms, and analysis of linear, time-invariant systems. Applications drawn broadly from engineering and physics, including audio and image processing, communications, and automatic control.'});
db.courses.insert({'name': 'Computation Structures', 'tags': ['REST'], 'title': '6.004 Computation Structures', 'total_units': 12, 'course_numbers': ['6.004'], 'units': '4-0-8', 'description': 'Introduces architecture of digital systems, emphasizing structural principles common to a wide range of technologies. Multilevel implementation strategies; definition of new primitives (e.g., gates, instructions, procedures, and processes) and their mechanization using lower-level elements. Analysis of potential concurrency; precedence constraints and performance measures; pipelined and multidimensional systems. Instruction set design issues; architectural support for contemporary software structures.'});
db.courses.insert({'name': 'Elements of Software Construction', 'tags': ['REST'], 'title': '6.005 Elements of Software Construction', 'total_units': 12, 'course_numbers': ['6.005'], 'units': '4-0-8', 'description': 'Introduces fundamental principles and techniques of software development, i.e., how to write software that is safe from bugs, easy to understand, and ready for change. Topics include specifications and invariants; testing, test-case generation, and coverage; abstract data types and representation independence; design patterns for object-oriented programming; concurrent programming, including message passing and shared concurrency, and defending against races and deadlock; and functional programming with immutable data and higher-order functions. Includes weekly programming exercises and larger group programming projects. 12 Engineering Design Points.'});
db.courses.insert({'description': 'Introduction to mathematical modeling of computational problems, as well as common algorithms, algorithmic paradigms, and data structures used to solve these problems. Emphasizes the relationship between algorithms and programming, and introduces basic performance measures and analysis techniques for these problems.', 'title': '6.006 Introduction to Algorithms', 'total_units': 12, 'course_numbers': ['6.006'], 'units': '4-0-8', 'name': 'Introduction to Algorithms'});
db.courses.insert({'name': 'Electromagnetic Energy: From Motors to Solar Cells', 'tags': ['REST'], 'title': '6.007 Electromagnetic Energy: From Motors to Solar Cells', 'total_units': 12, 'course_numbers': ['6.007'], 'units': '5-1-6', 'description': 'Discusses applications of electromagnetic and equivalent quantum mechanical principles to classical and modern devices. Covers energy conversion and power flow in both macroscopic and quantum-scale electrical and electromechanical systems, including electric motors and generators, electric circuit elements, quantum tunneling structures and instruments. Studies photons as waves and particles and their interaction with matter in optoelectronic devices, including solar cells and displays.'});
db.courses.insert({'name': 'Introduction to Inference', 'tags': ['Institute LAB'], 'title': '6.008 Introduction to Inference', 'total_units': 12, 'course_numbers': ['6.008'], 'units': '4-4-4', 'description': 'Introduces probabilistic modeling for problems of inference and machine learning from data, emphasizing analytical and computational aspects. Distributions, marginalization, conditioning, and structure; graphical representations. Belief propagation, decision-making, classification, estimation, and prediction. Sampling methods and analysis. Introduces asymptotic analysis and information measures. Substantial computational laboratory component explores the concepts introduced in class in the context of realistic contemporary applications. Students design inference algorithms, investigate their behavior on real data, and discuss experimental results.'});
db.courses.insert({'name': 'Fundamentals of Programming (6.S04)', 'tags': ['Institute LAB'], 'title': '6.009 Fundamentals of Programming (6.S04)', 'total_units': 12, 'course_numbers': ['6.009'], 'units': '2-4-6', 'description': 'Introduces fundamental concepts of programming. Designed to develop skills in applying basic methods from programming languages to abstract problems. Topics include programming and Python basics, computational concepts, software engineering, algorithmic techniques, data types, and recursion and tail recursion. Lab component consists of software design, construction, and implementation of design.'});
db.courses.insert({'name': 'Introduction to EECS via Robot Sensing, Software and Control', 'tags': ['Institute LAB'], 'title': '6.01 Introduction to EECS via Robot Sensing, Software and Control', 'total_units': 12, 'course_numbers': ['6.01'], 'units': '2-4-6', 'description': 'An integrated introduction to electrical engineering and computer science, taught using substantial laboratory experiments with mobile robots. Key issues in the design of engineered artifacts operating in the natural world: measuring and modeling system behaviors; assessing errors in sensors and effectors; specifying tasks; designing solutions based on analytical and computational models; planning, executing, and evaluating experimental tests of performance; refining models and designs. Issues addressed in the context of computer programs, control systems, probabilistic inference problems, circuits and transducers, which all play important roles in achieving robust operation of a large variety of engineered systems.'});
db.courses.insert({'description': 'Covers signals, systems and inference in communication, control and signal processing. Topics include input-output and state-space models of linear systems driven by deterministic and random signals; time- and transform-domain representations in discrete and continuous time; and group delay. State feedback and observers. Probabilistic models; stochastic processes, correlation functions, power spectra, spectral factorization. Least-mean square error estimation; Wiener filtering. Hypothesis testing; detection; matched filters.', 'title': '6.011 Signals, Systems and Inference', 'total_units': 12, 'course_numbers': ['6.011'], 'units': '4-0-8', 'name': 'Signals, Systems and Inference'});
db.courses.insert({'description': 'Microelectronic device modeling, and basic microelectronic circuit analysis and design. Physical electronics of semiconductor junction and MOS devices. Relating terminal behavior to internal physical processes, developing circuit models, and understanding the uses and limitations of different models. Use of incremental and large-signal techniques to analyze and design transistor circuits, with examples chosen from digital circuits, linear amplifiers, and other integrated circuits. Design project.', 'title': '6.012 Microelectronic Devices and Circuits', 'total_units': 12, 'course_numbers': ['6.012'], 'units': '4-0-8', 'name': 'Microelectronic Devices and Circuits'});
db.courses.insert({'description': "Analysis and design of modern applications that employ electromagnetic phenomena for signals and power transmission in RF, microwaves, optical and wireless communication systems. Fundamentals include dynamic solutions for Maxwell's equations; electromagnetic power and energy, waves in media, guided waves, radiation, and diffraction; coupling to media and structures; resonance  filters; acoustic analogs. Labs include student hands-on activities from building to testing of devices and systems (e.g. radar) that reinforce lectures, with a focus on fostering creativity and debugging skills. 6.002 and 6.007 are recommended but not required.", 'title': '6.013 Electromagnetics and Applications', 'total_units': 12, 'course_numbers': ['6.013'], 'units': '3-3-6', 'name': 'Electromagnetics and Applications'});
db.courses.insert({'name': 'Introduction to EECS via Communications Networks', 'tags': ['Institute LAB'], 'title': '6.02 Introduction to EECS via Communications Networks', 'total_units': 12, 'course_numbers': ['6.02'], 'units': '4-4-4', 'description': 'Studies key concepts, systems, and algorithms to reliably communicate data in settings ranging from the cellular phone network and the Internet to deep space. Weekly laboratory experiments explore these areas in depth. Topics presented in three modules - bits, signals, and packets - spanning the multiple layers of a communication system. Bits module includes information, entropy, data compression algorithms, and error correction with block and convolutional codes. Signals module includes modeling physical channels and noise, signal design, filtering and detection, modulation, and frequency-division multiplexing. Packets module includes switching and queuing principles, media access control, routing protocols, and data transport protocols.'});
db.courses.insert({'description': 'Integrated overview of the biophysics of cells from prokaryotes to neurons, with a focus on mass transport and electrical signal generation across cell membrane. First half of course focuses on mass transport through membranes: diffusion, osmosis, chemically mediated, and active transport. Second half focuses on electrical properties of cells: ion transport to action potentials in electrically excitable cells. Synaptic transmission. Electrical properties interpreted via kinetic and molecular properties of single voltage-gated ion channels. Laboratory and computer exercises illustrate the concepts. Students taking graduate version complete different assignments.', 'title': '6.021[J] Cellular Neurophysiology', 'total_units': 12, 'course_numbers': ['6.021[J]'], 'units': '5-2-5', 'name': 'Cellular Neurophysiology'});
db.courses.insert({'description': 'Application of the principles of energy and mass flow to major human organ systems. Mechanisms of regulation and homeostasis. Anatomical, physiological and pathophysiological features of the cardiovascular, respiratory and renal systems. Systems, features and devices that are most illuminated by the methods of physical sciences. Laboratory work includes some animal studies. Students taking graduate version complete additional assignments. 2 Engineering Design Points.', 'title': '6.022[J] Quantitative Systems Physiology', 'total_units': 12, 'course_numbers': ['6.022[J]'], 'units': '4-2-6', 'name': 'Quantitative Systems Physiology'});
db.courses.insert({'description': 'See description under subject 20.330[J].', 'title': '6.023[J] Fields, Forces and Flows in Biological Systems', 'total_units': 12, 'course_numbers': ['6.023[J]'], 'units': '4-0-8', 'name': 'Fields, Forces and Flows in Biological Systems'});
db.courses.insert({'description': 'See description under subject 20.310[J].', 'title': '6.024[J] Molecular, Cellular, and Tissue Biomechanics', 'total_units': 12, 'course_numbers': ['6.024[J]'], 'units': '4-0-8', 'name': 'Molecular, Cellular, and Tissue Biomechanics'});
db.courses.insert({'description': 'See description under subject 2.750[J].', 'title': '6.025[J] Medical Device Design', 'total_units': 12, 'course_numbers': ['6.025[J]'], 'units': '3-0-9', 'name': 'Medical Device Design'});
db.courses.insert({'description': 'See description under subject 2.180[J].', 'title': '6.027[J] Biomolecular Feedback Systems', 'total_units': 12, 'course_numbers': ['6.027[J]'], 'units': '3-0-9', 'name': 'Biomolecular Feedback Systems'});
db.courses.insert({'name': 'Introduction to EECS via Medical Technology', 'tags': ['Institute LAB'], 'title': '6.03 Introduction to EECS via Medical Technology', 'total_units': 12, 'course_numbers': ['6.03'], 'units': '4-4-4', 'description': 'Explores biomedical signals generated from electrocardiograms, glucose detectors or ultrasound images, and magnetic resonance images. Topics include physical characterization and modeling of systems in the time and frequency domains; analog and digital signals and noise; basic machine learning including decision trees, clustering, and classification; and introductory machine vision. Labs designed to strengthen background in signal processing and machine learning. Students design and run structured experiments, and develop and test procedures through further experimentation.'});
db.courses.insert({'name': 'Elements of Software Construction (New)', 'tags': [''], 'title': '6.031 Elements of Software Construction (New)', 'total_units': 6, 'course_numbers': ['6.031'], 'units': '5-0-1', 'description': 'Introduces fundamental principles and techniques of software development: how to write software that is safe from bugs, easy to understand, and ready for change. Topics include specifications and invariants; testing, test-case generation, and coverage; abstract data types and representation independence; design patterns for object-oriented programming; concurrent programming, including message passing and shared concurrency, and defending against races and deadlock; and functional programming with immutable data and higher-order functions. Includes weekly programming exercises and larger group programming projects.'});
db.courses.insert({'description': 'Topics on the engineering of computer software and hardware systems: techniques for controlling complexity; strong modularity using client-server design, operating systems; performance, networks; naming; security and privacy; fault-tolerant systems, atomicity and coordination of concurrent activities, and recovery; impact of computer systems on society. Case studies of working systems and readings from the current literature provide comparisons and contrasts. Includes a single, semester-long design project. Students engage in extensive written communication exercises.', 'title': '6.033 Computer System Engineering', 'total_units': 12, 'course_numbers': ['6.033'], 'units': '5-1-6', 'name': 'Computer System Engineering'});
db.courses.insert({'description': 'Introduces representations, methods, and architectures used to build applications and to account for human intelligence from a computational point of view. Covers applications of rule chaining, constraint propagation, constrained search, inheritance, statistical inference, and other problem-solving paradigms. Also addresses applications of identification trees, neural nets, genetic algorithms, support-vector machines, boosting, and other learning paradigms. Considers what separates human intelligence from that of other animals.', 'title': '6.034 Artificial Intelligence', 'total_units': 12, 'course_numbers': ['6.034'], 'units': '4-3-5', 'name': 'Artificial Intelligence'});
db.courses.insert({'description': 'Analyzes issues associated with the implementation of higher-level programming languages. Fundamental concepts, functions, and structures of compilers. The interaction of theory and practice. Using tools in building software. Includes a multi-person project on compiler design and implementation.', 'title': '6.035 Computer Language Engineering', 'total_units': 12, 'course_numbers': ['6.035'], 'units': '4-4-4', 'name': 'Computer Language Engineering'});
db.courses.insert({'description': 'Introduces principles, algorithms, and applications of machine learning from the point of view of modeling and prediction; formulation of learning problems; representation, over-fitting, generalization; clustering, classification, probabilistic modeling; and methods such as support vector machines, hidden Markov models, and Bayesian networks. Students taking graduate version complete additional assignments.', 'title': '6.036 Introduction to Machine Learning', 'total_units': 12, 'course_numbers': ['6.036'], 'units': '4-0-8', 'name': 'Introduction to Machine Learning'});
db.courses.insert({'description': 'Studies the structure and interpretation of computer programs which transcend specific programming languages. Demonstrates thought patterns for computer science using Scheme. Includes weekly programming projects.', 'title': '6.037 Structure and Interpretation of Computer Programs', 'total_units': 6, 'course_numbers': ['6.037'], 'units': '1-0-5', 'name': 'Structure and Interpretation of Computer Programs'});
db.courses.insert({'description': 'Provides an introduction to probability theory and the modeling and analysis of probabilistic systems. Probabilistic models, conditional probability. Discrete and continuous random variables. Expectation and conditional expectation. Limit Theorems. Students taking graduate version complete additional assignments. Combination of 6.041A and 6.041B counts as a REST subject.', 'title': '6.041A Introduction to Probability I (New)', 'total_units': 6, 'course_numbers': ['6.041A'], 'units': '2-0-4', 'name': 'Introduction to Probability I (New)'});
db.courses.insert({'description': 'Building on 6.041A, further discusses topics in probability. Bayesian estimation and hypothesis testing. Elements of statistical inference. Bernoulli and Poisson processes. Markov chains. Students taking graduate version complete additional assignments. Combination of 6.041A and 6.041B counts as a REST subject.', 'title': '6.041B Introduction to Probability II (New)', 'total_units': 6, 'course_numbers': ['6.041B'], 'units': '2-0-4', 'name': 'Introduction to Probability II (New)'});
db.courses.insert({'name': 'Mathematics for Computer Science', 'tags': ['REST'], 'title': '6.042[J] Mathematics for Computer Science', 'total_units': 12, 'course_numbers': ['6.042[J]'], 'units': '5-0-7', 'description': 'Elementary discrete mathematics for computer science and engineering. Emphasis on mathematical definitions and proofs as well as on applicable methods. Topics include formal logic notation, proof methods; induction, well-ordering; sets, relations; elementary graph theory; asymptotic notation and growth of functions; permutations and combinations, counting principles; discrete probability. Further selected topics include recursive definition and structural induction, state machines and invariants, integer congruences, recurrences, generating functions.'});
db.courses.insert({'description': 'Provides an introduction to some of the central ideas of theoretical computer science, including circuits, finite automata, Turing machines and computability, efficient algorithms and reducibility, the P versus NP problem, NP-completeness, the power of randomness, cryptography, computational learning theory, and quantum computing. Examines the classes of problems that can and cannot be solved in various computational models.', 'title': '6.045[J] Automata, Computability, and Complexity', 'total_units': 12, 'course_numbers': ['6.045[J]'], 'units': '4-0-8', 'name': 'Automata, Computability, and Complexity'});
db.courses.insert({'description': 'Techniques for the design and analysis of efficient algorithms, emphasizing methods useful in practice. Topics include sorting; search trees, heaps, and hashing; divide-and-conquer; dynamic programming; greedy algorithms; amortized analysis; graph algorithms; and shortest paths. Advanced topics may include network flow; computational geometry; number-theoretic algorithms; polynomial and matrix calculations; caching; and parallel computing.', 'title': '6.046[J] Design and Analysis of Algorithms', 'total_units': 12, 'course_numbers': ['6.046[J]'], 'units': '4-0-8', 'name': 'Design and Analysis of Algorithms'});
db.courses.insert({'description': 'Covers the algorithmic and machine learning foundations of computational biology, combining theory with practice. Principles of algorithm design, influential problems and techniques, and analysis of large-scale biological datasets. Topics include (a) genomes: sequence analysis, gene finding, RNA folding, genome alignment and assembly, database search; (b) networks: gene expression analysis, regulatory motifs, biological network analysis; (c) evolution: comparative genomics, phylogenetics, genome duplication, genome rearrangements, evolutionary theory. These are coupled with fundamental algorithmic techniques including: dynamic programming, hashing, Gibbs sampling, expectation maximization, hidden Markov models, stochastic context-free grammars, graph clustering, dimensionality reduction, Bayesian networks.', 'title': '6.047 Computational Biology: Genomes, Networks, Evolution', 'total_units': 12, 'course_numbers': ['6.047'], 'units': '3-0-9', 'name': 'Computational Biology: Genomes, Networks, Evolution'});
db.courses.insert({'description': 'See description under subject 7.33[J].', 'title': '6.049[J] Evolutionary Biology: Concepts, Models and Computation', 'total_units': 12, 'course_numbers': ['6.049[J]'], 'units': '3-0-9', 'name': 'Evolutionary Biology: Concepts, Models and Computation'});
db.courses.insert({'description': 'Explores the ultimate limits to communication and computation, with an emphasis on the physical nature of information and information processing. Topics include information and computation, digital signals, codes, and compression. Biological representations of information. Logic circuits, computer architectures, and algorithmic information. Noise, probability, and error correction. The concept of entropy applied to channel capacity and to the second law of thermodynamics. Reversible and irreversible operations and the physics of computation. Quantum computation.', 'title': '6.050[J] Information, Entropy, and Computation', 'total_units': 9, 'course_numbers': ['6.050[J]'], 'units': '3-0-6', 'name': 'Information, Entropy, and Computation'});
db.courses.insert({'description': 'Accelerated introduction to MATLAB and its popular toolboxes. Lectures are interactive, with students conducting sample MATLAB problems in real time. Includes problem-based MATLAB assignments. Students must provide their own laptop and software.', 'title': '6.057 Introduction to MATLAB', 'total_units': 3, 'course_numbers': ['6.057'], 'units': '1-0-2', 'name': 'Introduction to MATLAB'});
db.courses.insert({'description': 'Introduces fundamental concepts for 6.003, including Fourier and Laplace transforms, convolution, sampling, filters, feedback control, stability, and Bode plots. Students engage in problem solving, using Mathematica and MATLAB software extensively to help visualize processing in the time frequency domains.', 'title': '6.058 Introduction to Signals and Systems, and Feedback Control', 'total_units': 6, 'course_numbers': ['6.058'], 'units': '2-2-2', 'name': 'Introduction to Signals and Systems, and Feedback Control'});
db.courses.insert({'description': 'Electric circuit theory with application to power handling electric circuits. Modeling and behavior of electromechanical devices, including magnetic circuits, motors, and generators. Operational fundamentals of synchronous, induction and DC machinery. Interconnection of generators and motors with electric power transmission and distribution circuits. Power generation, including alternative and sustainable sources. Students taking graduate version complete additional assignments.', 'title': '6.061 Introduction to Electric Power Systems', 'total_units': 12, 'course_numbers': ['6.061'], 'units': '3-0-9', 'name': 'Introduction to Electric Power Systems'});
db.courses.insert({'description': 'Intuition-based introduction to electronics, electronic components and test equipment such as oscilloscopes, meters (voltage, resistance inductance, capacitance, etc.), and signal generators. Emphasizes individual instruction and development of skills, such as soldering, assembly, and troubleshooting. Students design, build, and keep a small electronics project to put their new knowledge into practice. Intended for students with little or no previous background in electronics.', 'title': '6.070[J] Electronics Project Laboratory', 'total_units': 6, 'course_numbers': ['6.070[J]'], 'units': '2-2-2', 'name': 'Electronics Project Laboratory'});
db.courses.insert({'description': 'See description under subject EC.110[J].', 'title': '6.072[J] Introduction to Digital Electronics', 'total_units': 6, 'course_numbers': ['6.072[J]'], 'units': '0-3-3', 'name': 'Introduction to Digital Electronics'});
db.courses.insert({'name': 'Creating Video Games', 'tags': ['HASS'], 'title': '6.073[J] Creating Video Games', 'total_units': 12, 'course_numbers': ['6.073[J]'], 'units': '3-3-6', 'description': 'See description under subject CMS.611[J].'});
db.courses.insert({'units': 'Can be repeated for credit.', 'description': 'Basic undergraduate subjects not offered in the regular curriculum.', 'name': 'Special Subject in Electrical Engineering and Computer Science', 'course_numbers': ['6.S062'], 'title': '6.S062 Special Subject in Electrical Engineering and Computer Science'});
db.courses.insert({'units': 'Can be repeated for credit.', 'description': 'Basic undergraduate subjects not offered in the regular curriculum.', 'name': '6.S064 Special Subject in Electrical Engineering and Computer Science', 'course_numbers': ['6.S063,'], 'title': '6.S063, 6.S064 Special Subject in Electrical Engineering and Computer Science'});
db.courses.insert({'name': 'Special Subject: Interconnected Embedded Systems', 'tags': ['Institute LAB'], 'title': '6.S08 Special Subject: Interconnected Embedded Systems', 'total_units': 12, 'course_numbers': ['6.S08'], 'units': '1-5-6', 'description': 'Introduction to embedded systems in the context of connected devices, wearables and the Internet of Things. Topics include microcontrollers, energy utilization, algorithmic efficiency, interfacing with sensors, networking, cryptography, local versus distributed computation, data analytics, and 3D printing. Students will design, make, and program an internet-connected wearable device. Final project where student teams will design and demo their own cloud-connected wearable system. Licensed for Spring 2016 by the Committee on Curricula.'});
db.courses.insert({'units': 'Can be repeated for credit.', 'description': 'Covers subject matter not offered in the regular curriculum. Consult department to learn of offerings for a particular term.', 'name': 'Special Subject in Electrical Engineering and Computer Science', 'course_numbers': ['6.S076-6.S084'], 'title': '6.S076-6.S084 Special Subject in Electrical Engineering and Computer Science'});
db.courses.insert({'units': 'Can be repeated for credit.', 'description': 'Covers subject matter not offered in the regular curriculum. Consult department to learn of offerings for a particular term.', 'name': 'Special Subject in Electrical Engineering and Computer Science', 'course_numbers': ['6.S085-6.S099'], 'title': '6.S085-6.S099 Special Subject in Electrical Engineering and Computer Science'});
db.courses.insert({'units': 'Can be repeated for credit.', 'description': 'Individual experimental work related to electrical engineering and computer science. Student must make arrangements with a project supervisor and file a proposal endorsed by the supervisor. Departmental approval required. Written report to be submitted upon completion of work.', 'name': 'Electrical Engineering and Computer Science Project', 'course_numbers': ['6.100'], 'title': '6.100 Electrical Engineering and Computer Science Project'});
db.courses.insert({'name': 'Introductory Analog Electronics Laboratory', 'tags': ['Institute LAB'], 'title': '6.101 Introductory Analog Electronics Laboratory', 'total_units': 12, 'course_numbers': ['6.101'], 'units': '2-9-1', 'description': 'Introductory experimental laboratory explores the design, construction, and debugging of analog electronic circuits. Lectures and laboratory projects in the first half of the course investigate the performance characteristics of semiconductor devices (diodes, BJTs, and MOSFETs) and functional analog building blocks, including single-stage amplifiers, op amps, small audio amplifier, filters, converters, sensor circuits, and medical electronics (ECG, pulse-oximetry). Projects involve design, implementation, and presentation in an environment similar to that of industry engineering design teams. Instruction and practice in written and oral communication provided. Opportunity to simulate real-world problems and solutions that involve tradeoffs and the use of engineering judgment. Engineers from local companies help students with their design projects.'});
db.courses.insert({'name': 'Introductory Digital Systems Laboratory', 'tags': ['Institute LAB'], 'title': '6.111 Introductory Digital Systems Laboratory', 'total_units': 12, 'course_numbers': ['6.111'], 'units': '3-7-2', 'description': 'Lectures and labs on digital logic, flip flops, PALs, FPGAs, counters, timing, synchronization, and finite-state machines prepare students for the design and implementation of a final project of their choice: games, music, digital filters, wireless communications, video, or graphics. Extensive use of Verilog for describing and implementing digital logic designs.'});
db.courses.insert({'name': 'Microcomputer Project Laboratory', 'tags': ['Institute LAB'], 'title': '6.115 Microcomputer Project Laboratory', 'total_units': 12, 'course_numbers': ['6.115'], 'units': '3-6-3', 'description': "Introduces analysis and design of embedded systems. Microcontrollers provide adaptation, flexibility, and real-time control. Emphasizes construction of complete systems, including a five-axis robot arm, a fluorescent lamp ballast, a tomographic imaging station (e.g., a CAT scan), and a simple calculator. Presents a wide range of basic tools, including software and development tools, programmable system on chip, peripheral components such as A/D converters, communication schemes, signal processing techniques, closed-loop digital feedback control, interface and power electronics, and modeling of electromechanical systems. Includes a sequence of assigned projects, followed by a final project of the student's choice, emphasizing creativity and uniqueness. Provides instruction in written and oral communication. Students taking independent inquiry version 6.1151 expand the scope of their laboratory project."});
db.courses.insert({'description': "Introduces analysis and design of embedded systems. Microcontrollers provide adaptation, flexibility, and real-time control. Emphasizes construction of complete systems, including a five-axis robot arm, a fluorescent lamp ballast, a tomographic imaging station (e.g., a CAT scan), and a simple calculator. Presents a wide range of basic tools, including software and development tools, programmable system on chip, peripheral components such as A/D converters, communication schemes, signal processing techniques, closed-loop digital feedback control, interface and power electronics, and modeling of electromechanical systems. Includes a sequence of assigned projects, followed by a final project of the student's choice, emphasizing creativity and uniqueness. Provides instruction in written and oral communication. Students taking independent inquiry version 6.1151 expand the scope of their laboratory project.", 'title': '6.1151 Microcomputer Project Laboratory - Independent Inquiry (New)', 'total_units': 15, 'course_numbers': ['6.1151'], 'units': '3-9-3', 'name': 'Microcomputer Project Laboratory - Independent Inquiry (New)'});
db.courses.insert({'description': 'Introduces basic electrical engineering concepts, components, and laboratory techniques. Covers analog integrated circuits, power supplies, and digital circuits. Lab exercises provide practical experience in constructing projects using multi-meters, oscilloscopes, logic analyzers, and other tools. Includes a project in which students build a circuit to display their own EKG.', 'title': '6.117 Introduction to Electrical Engineering Lab Skills', 'total_units': 6, 'course_numbers': ['6.117'], 'units': '1-3-2', 'name': 'Introduction to Electrical Engineering Lab Skills'});
db.courses.insert({'description': 'See description under subject 20.345[J].', 'title': '6.123[J] Bioinstrumentation Project Lab', 'total_units': 12, 'course_numbers': ['6.123[J]'], 'units': '2-7-3', 'name': 'Bioinstrumentation Project Lab'});
db.courses.insert({'name': 'Biological Circuit Engineering Laboratory', 'tags': ['Institute LAB'], 'title': '6.129[J] Biological Circuit Engineering Laboratory', 'total_units': 12, 'course_numbers': ['6.129[J]'], 'units': '2-8-2', 'description': 'Students assemble individual genes and regulatory elements into larger-scale circuits; they experimentally characterize these circuits in yeast cells using quantitative techniques, including flow cytometry, and model their results computationally. Emphasizes concepts and techniques to perform independent experimental and computational synthetic biology research. Discusses current literature and ongoing research in the field of synthetic biology. Instruction and practice in oral and written communication provided.'});
db.courses.insert({'name': 'Power Electronics Laboratory', 'tags': ['Institute LAB'], 'title': '6.131 Power Electronics Laboratory', 'total_units': 12, 'course_numbers': ['6.131'], 'units': '3-6-3', 'description': 'Introduces the design and construction of power electronic circuits and motor drives. Laboratory exercises include the construction of drive circuitry for an electric go-cart, flash strobes, computer power supplies, three-phase inverters for AC motors, and resonant drives for lamp ballasts and induction heating. Basic electric machines introduced include DC, induction, and permanent magnet motors, with drive considerations. Provides instruction in written and oral communication. Students taking independent inquiry version 6.1131 expand the scope of their laboratory project.'});
db.courses.insert({'description': 'Introduces the design and construction of power electronic circuits and motor drives. Laboratory exercises include the construction of drive circuitry for an electric go-cart, flash strobes, computer power supplies, three-phase inverters for AC motors, and resonant drives for lamp ballasts and induction heating. Basic electric machines introduced include DC, induction, and permanent magnet motors, with drive considerations. Provides instruction in written and oral communication. Students taking independent inquiry version 6.1131 expand the scope of their laboratory project.', 'title': '6.1311 Power Electronics Laboratory - Independent Inquiry (New)', 'total_units': 15, 'course_numbers': ['6.1311'], 'units': '3-9-3', 'name': 'Power Electronics Laboratory - Independent Inquiry (New)'});
db.courses.insert({'name': 'Robotics: Science and Systems', 'tags': ['Institute LAB'], 'title': '6.141[J] Robotics: Science and Systems', 'total_units': 12, 'course_numbers': ['6.141[J]'], 'units': '2-6-4', 'description': 'Presents concepts, principles, and algorithms for sensing and computation related to the physical world. Topics include motion planning, geometric reasoning, kinematics and dynamics, state estimation, tracking, map building, manipulation, human-robot interaction, fault diagnosis, and embedded system development. Students specify and design a small-scale yet complex robot capable of real-time interaction with the natural world. Students engage in extensive written and oral communication exercises.'});
db.courses.insert({'description': 'Autonomous robotics contest emphasizing technical AI, vision, mapping and navigation from a robot-mounted camera. Few restrictions are placed on materials, sensors, and/or actuators enabling teams to build robots very creatively. Teams should have members with varying engineering, programming and mechanical backgrounds. Culminates with a robot competition at the end of IAP.', 'title': '6.146 Mobile Autonomous Systems Laboratory: MASLAB', 'total_units': 6, 'course_numbers': ['6.146'], 'units': 'Can be repeated for credit.', 'name': 'Mobile Autonomous Systems Laboratory: MASLAB'});
db.courses.insert({'description': 'Artificial Intelligence programming contest in Java. Student teams program virtual robots to play Battlecode, a real-time strategy game. Competition culminates in a live BattleCode tournament. Assumes basic knowledge of programming.', 'title': '6.147 The Battlecode Programming Competition', 'total_units': 6, 'course_numbers': ['6.147'], 'units': 'Can be repeated for credit.', 'name': 'The Battlecode Programming Competition'});
db.courses.insert({'description': 'Teams compete to build the most functional and user-friendly website. Competition is judged by industry experts and includes novice and advanced divisions. Prizes awarded. Lectures and workshops cover website basics.', 'title': '6.148 Web Programming Competition', 'total_units': 6, 'course_numbers': ['6.148'], 'units': 'Can be repeated for credit.', 'name': 'Web Programming Competition'});
db.courses.insert({'description': 'Fact-paced introduction to Python programming language for students with little or no programming experience. Covers both function and object-oriented concepts. Includes weekly lab exercises and final project.', 'title': '6.149 Introduction to Programming Using Python', 'total_units': 6, 'course_numbers': ['6.149'], 'units': '3-0-3', 'name': 'Introduction to Programming Using Python'});
db.courses.insert({'description': 'Student teams design and build an Android application based on a given theme. Lectures and labs led by experienced students and leading industry experts, covering the basics of Android development, concepts and tools to help participants build great apps. Contest culminates with a public presentation in front of a judging panel comprised of professional developers and MIT faculty. Prizes awarded.', 'title': '6.150 Mobile Applications Competition', 'total_units': 6, 'course_numbers': ['6.150'], 'units': 'Can be repeated for credit.', 'name': 'Mobile Applications Competition'});
db.courses.insert({'description': 'Introduction to iOS game design and development for students already familiar with object-oriented programming. Provides a set of basic tools (Objective-C and Cocos2D) and exposure to real-world issues in game design. Working in small teams, students complete a final project in which they create their own iPhone game. At the end of IAP, teams present their games in competition for prizes awarded by a judging panel of gaming experts.', 'title': '6.151 iOS Game Design and Development Competition', 'total_units': 6, 'course_numbers': ['6.151'], 'units': '2-2-2', 'name': 'iOS Game Design and Development Competition'});
db.courses.insert({'description': 'Introduces the theory and technology of micro/nano fabrication. Lectures and laboratory sessions on basic processing techniques such as vacuum processes, lithography, diffusion, oxidation, and pattern transfer. Students fabricate MOS capacitors, nanomechanical cantilevers, and microfluidic mixers. Emphasis on the interrelationships between material properties and processing, device structure, and the electrical, mechanical, optical, chemical or biological behavior of devices. Provides background for thesis work in micro/nano fabrication. Students engage in extensive written and oral communication exercises.', 'title': '6.152[J] Micro/Nano Processing Technology', 'total_units': 12, 'course_numbers': ['6.152[J]'], 'units': '3-4-5', 'name': 'Micro/Nano Processing Technology'});
db.courses.insert({'name': 'Modern Optics Project Laboratory', 'tags': ['Institute LAB'], 'title': '6.161 Modern Optics Project Laboratory', 'total_units': 12, 'course_numbers': ['6.161'], 'units': '3-5-4', 'description': 'Lectures, laboratory exercises and projects on optical signal generation, transmission, detection, storage, processing and display. Topics include polarization properties of light; reflection and refraction; coherence and interference; Fraunhofer and Fresnel diffraction; holography; Fourier optics; coherent and incoherent imaging and signal processing systems; optical properties of materials; lasers and LEDs; electro-optic and acousto-optic light modulators; photorefractive and liquid-crystal light modulation; display technologies; optical waveguides and fiber-optic communication systems; photodetectors. Students may use this subject to find an advanced undergraduate project. Students engage in extensive oral and written communcation exercises. Recommended prerequisites: 6.007 or 8.03.'});
db.courses.insert({'name': 'Strobe Project Laboratory', 'tags': ['Institute LAB'], 'title': '6.163 Strobe Project Laboratory', 'total_units': 12, 'course_numbers': ['6.163'], 'units': '2-8-2', 'description': 'Application of electronic flash sources to measurement and photography. First half covers fundamentals of photography and electronic flashes, including experiments on application of electronic flash to photography, stroboscopy, motion analysis, and high-speed videography. Students write four extensive lab reports. In the second half, students work in small groups to select, design, and execute independent projects in measurement or photography that apply learned techniques. Project planning and execution skills are discussed and developed over the term. Students engage in extensive written and oral communication exercises.'});
db.courses.insert({'description': 'Building on the framework of 6.002, provides a deeper understanding of the theory and applications of circuits and electronics.', 'title': '6.169 Theory and Application of Circuits and Electronics', 'total_units': 3, 'course_numbers': ['6.169'], 'units': '1-1-1', 'name': 'Theory and Application of Circuits and Electronics'});
db.courses.insert({'description': 'Covers design and implementation of software systems, using web applications as the platform. Emphasizes the role of conceptual design in achieving clarity, simplicity, and modularity. Students complete open-ended individual assignments and a major team project.', 'title': '6.170 Software Studio', 'total_units': 12, 'course_numbers': ['6.170'], 'units': '4-0-8', 'name': 'Software Studio'});
db.courses.insert({'description': 'Project-based introduction to building efficient, high-performance and scalable software systems. Topics include performance analysis, algorithmic techniques for high performance, instruction-level optimizations, vectorization, cache and memory hierarchy optimization, and parallel programming. Students taking graduate version complete additional assignments.', 'title': '6.172 Performance Engineering of Software Systems', 'total_units': 18, 'course_numbers': ['6.172'], 'units': '3-12-', 'name': 'Performance Engineering of Software Systems'});
db.courses.insert({'description': 'Illustrates a constructive (as opposed to a descriptive) approach to computer architecture. Topics include combinational and pipelined arithmetic-logic units (ALU), in-order pipelined microarchitectures, branch prediction, blocking and unblocking caches, interrupts, virtual memory support, cache coherence and multicore architectures. Labs in a modern Hardware Design Language (HDL) illustrate various aspects of microprocessor design, culminating in a term project in which students present a multicore design running on an FPGA board.', 'title': '6.175 Constructive Computer Architecture', 'total_units': 12, 'course_numbers': ['6.175'], 'units': '3-8-1', 'name': 'Constructive Computer Architecture'});
db.courses.insert({'description': 'Build autonomous poker players and aquire the knowledge of the game of poker. Showcase decision making skills, apply concepts in mathematics, computer science and economics. Provides instruction in programming, game theory, probability and statistics and machine learning. Concludes with a final competition and prizes.', 'title': '6.176 Pokerbots Competition', 'total_units': 6, 'course_numbers': ['6.176'], 'units': 'Can be repeated for credit.', 'name': 'Pokerbots Competition'});
db.courses.insert({'description': 'Preparation for 6.01 aimed to sharpen skills in program design, implementation, and debugging in Python. Programming intensive, with one short structured assignment and a supervised, but highly individual, mandatory project presentation. Intended for students with some elementary programming experience (equivalent to AP Computer Science).', 'title': '6.177 Building Programming Experience in Python', 'total_units': 6, 'course_numbers': ['6.177'], 'units': '1-0-5', 'name': 'Building Programming Experience in Python'});
db.courses.insert({'description': 'Covers the fundamentals of Java, helping students develop intuition about object-oriented programming. Focuses on developing working software that solves real problems. Designed for students with little or no programming experience. Concepts covered useful to 6.005.', 'title': '6.178 Introduction to Software Engineering in Java', 'total_units': 6, 'course_numbers': ['6.178'], 'units': '1-1-4', 'name': 'Introduction to Software Engineering in Java'});
db.courses.insert({'description': 'Fast-paced introduction to the C and C++ programming languages. Intended for those with experience in other languages who have never used C or C++. Students complete daily assignments, a small-scale individual project, and a mandatory online diagnostic test.', 'title': '6.179 Introduction to C and C++', 'total_units': 6, 'course_numbers': ['6.179'], 'units': '3-3-0', 'name': 'Introduction to C and C++'});
db.courses.insert({'name': 'Psychoacoustics Project Laboratory', 'tags': ['Institute LAB'], 'title': '6.182 Psychoacoustics Project Laboratory', 'total_units': 12, 'course_numbers': ['6.182'], 'units': '3-6-3', 'description': 'Introduces the methods used to measure human auditory abilities. Discusses auditory function, principles of psychoacoustic measurement, models for psychoacoustic performance, and experimental techniques. Project topics: absolute and differential auditory sensitivity, operating characteristics of human observers, span of auditory judgment, adaptive measurement procedures, and scaling sensory magnitudes. Knowledge of probability helpful. Students engage in extensive written and oral communication exercises.'});
db.courses.insert({'units': 'Can be repeated for credit.', 'description': 'Laboratory subject that covers content not offered in the regular curriculum. Consult department to learn of offerings for a particular term.', 'name': 'Special Laboratory Subject in Electrical Engineering and Computer Science', 'course_numbers': ['6.S183-6.S192'], 'title': '6.S183-6.S192 Special Laboratory Subject in Electrical Engineering and Computer Science'});
db.courses.insert({'units': 'Can be repeated for credit.', 'description': 'Laboratory subject that covers content not offered in the regular curriculum. Consult department to learn of offerings for a particular term.', 'name': 'Special Laboratory Subject in Electrical Engineering and Computer Science', 'course_numbers': ['6.S193-6.S198'], 'title': '6.S193-6.S198 Special Laboratory Subject in Electrical Engineering and Computer Science'});
db.courses.insert({'description': 'Research project for those students completing the SB degree, to be arranged by the student and an appropriate MIT faculty member. Students who register for this subject must consult the department undergraduate office. Students engage in extensive written communications exercises.', 'title': '6.UAP Undergraduate Advanced Project', 'total_units': 6, 'course_numbers': ['6.UAP'], 'units': 'Can be repeated for credit.', 'name': 'Undergraduate Advanced Project'});
db.courses.insert({'description': 'Instruction in effective undergraduate research, including choosing and developing a research topic, surveying previous work and publications, research topics in EECS, industry best practices, design for robustness, technical presentation, authorship and collaboration, and ethics. Material covered over both fall and spring terms. Students engage in extensive written and oral communication exercises, in the context of an approved advanced research project. May be repeated for credit for a maximum of 12 units.', 'title': '6.UAR Seminar in Undergraduate Advanced Research', 'total_units': 6, 'course_numbers': ['6.UAR'], 'units': 'Can be repeated for credit.', 'name': 'Seminar in Undergraduate Advanced Research'});
db.courses.insert({'description': 'Provides instruction in aspects of effective technical oral presentations and exposure to communication skills useful in a workplace setting. Students create, give and revise a number of presentations of varying length targeting a range of different audiences.', 'title': '6.UAT Oral Communication', 'total_units': 9, 'course_numbers': ['6.UAT'], 'units': '3-0-6', 'name': 'Oral Communication'});
db.courses.insert({'units': 'Can be repeated for credit.', 'description': 'Year-long individual research project arranged with appropriate faculty member or approved supervisor. Forms and instructions for the proposal and final report are available in the EECS Undergraduate Office.', 'name': 'Undergraduate Research in Electrical Engineering and Computer Science', 'course_numbers': ['6.URS'], 'title': '6.URS Undergraduate Research in Electrical Engineering and Computer Science'});
db.courses.insert({'name': 'Networks', 'tags': ['HASS'], 'title': '6.207[J] Networks', 'total_units': 12, 'course_numbers': ['6.207[J]'], 'units': '4-0-8', 'description': 'See description under subject 14.15[J].'});
db.courses.insert({'description': 'Sequential decision-making via dynamic programming. Unified approach to optimal control of stochastic dynamic systems and Markovian decision problems. Applications in linear-quadratic control, inventory control, resource allocation, scheduling, and planning. Optimal decision making under perfect and imperfect state information. Certainty equivalent, open loop-feedback control, rollout, model predictive control, aggregation, and other suboptimal control methods. Infinite horizon problems: discounted, stochastic shortest path, average cost, and semi-Markov models. Value and policy iteration. Abstract models in dynamic programming. Approximate/neurodynamic programming. Simulation based methods. Discussion of current research on the solution of large-scale problems.', 'title': '6.231 Dynamic Programming and Stochastic Control', 'total_units': 12, 'course_numbers': ['6.231'], 'units': '3-0-9', 'name': 'Dynamic Programming and Stochastic Control'});
db.courses.insert({'description': 'Linear, discrete- and continuous-time, multi-input-output systems in control, related areas. Least squares and matrix perturbation problems. State-space models, modes, stability, controllability, observability, transfer function matrices, poles and zeros, and minimality. Internal stability of interconnected systems, feedback compensators, state feedback, optimal regulation, observers, and observer-based compensators. Measures of control performance, robustness issues using singular values of transfer functions. Introductory ideas on nonlinear systems. Recommended prerequisite: 6.302.', 'title': '6.241[J] Dynamic Systems and Control', 'total_units': 12, 'course_numbers': ['6.241[J]'], 'units': '4-0-8', 'name': 'Dynamic Systems and Control'});
db.courses.insert({'description': 'Computer-aided design methodologies for synthesis of multivariable feedback control systems. Performance and robustness trade-offs. Model-based compensators; Q-parameterization; ill-posed optimization problems; dynamic augmentation; linear-quadratic optimization of controllers; H-infinity controller design; Mu-synthesis; model and compensator simplification; nonlinear effects. Computer-aided (MATLAB) design homework using models of physical processes.', 'title': '6.245 Multivariable Control Systems', 'total_units': 12, 'course_numbers': ['6.245'], 'units': '3-0-9', 'name': 'Multivariable Control Systems'});
db.courses.insert({'description': 'Advanced study of topics in control. Specific focus varies from year to year.', 'title': '6.246, 6.247 Advanced Topics in Control', 'total_units': 12, 'course_numbers': ['6.246,'], 'units': 'Can be repeated for credit.', 'name': '6.247 Advanced Topics in Control'});
db.courses.insert({'description': 'Advanced study of topics in numerical methods. Specific focus varies from year to year.', 'title': '6.248, 6.249 Advanced Topics in Numerical Methods', 'total_units': 12, 'course_numbers': ['6.248,'], 'units': 'Can be repeated for credit.', 'name': '6.249 Advanced Topics in Numerical Methods'});
db.courses.insert({'description': 'Introduction to linear optimization and its extensions emphasizing both methodology and the underlying mathematical structures and geometrical ideas. Covers classical theory of linear programming as well as some recent advances in the field. Topics: simplex method; duality theory; sensitivity analysis; network flow problems; decomposition; integer programming; interior point algorithms for linear programming; and introduction to combinatorial optimization and NP-completeness.', 'title': '6.251[J] Introduction to Mathematical Programming', 'total_units': 12, 'course_numbers': ['6.251[J]'], 'units': '4-0-8', 'name': 'Introduction to Mathematical Programming'});
db.courses.insert({'description': 'Unified analytical and computational approach to nonlinear optimization problems. Unconstrained optimization methods include gradient, conjugate direction, Newton, sub-gradient and first-order methods. Constrained optimization methods include feasible directions, projection, interior point methods, and Lagrange multiplier methods. Convex analysis, Lagrangian relaxation, nondifferentiable optimization, and applications in integer programming. Comprehensive treatment of optimality conditions and Lagrange multipliers. Geometric approach to duality theory. Applications drawn from control, communications, power systems, and resource allocation problems.', 'title': '6.252[J] Nonlinear Optimization', 'total_units': 12, 'course_numbers': ['6.252[J]'], 'units': '4-0-8', 'name': 'Nonlinear Optimization'});
db.courses.insert({'description': 'Core analytical issues of continuous optimization, duality, and saddle point theory, and development using a handful of unifying principles that can be easily visualized and readily understood. Discusses in detail the mathematical theory of convex sets and functions which are the basis for an intuitive, highly visual, geometrical approach to the subject. Convex optimization algorithms focus on large-scale problems, drawn from several types of applications, such as resource allocation and machine learning. Includes batch and incremental subgradient, cutting plane, proximal, and bundle methods.', 'title': '6.253 Convex Analysis and Optimization', 'total_units': 12, 'course_numbers': ['6.253'], 'units': '3-0-9', 'name': 'Convex Analysis and Optimization'});
db.courses.insert({'description': 'Introduction to fundamentals of game theory and mechanism design with motivations for each topic drawn from engineering applications (including distributed control of wireline/wireless communication networks, transportation networks, pricing). Emphasis on the foundations of the theory, mathematical tools, as well as modeling and the equilibrium notion in different environments. Topics include normal form games, supermodular games, dynamic games, repeated games, games with incomplete/imperfect information, mechanism design, cooperative game theory, and network games.', 'title': '6.254 Game Theory with Engineering Applications', 'total_units': 12, 'course_numbers': ['6.254'], 'units': '4-0-8', 'name': 'Game Theory with Engineering Applications'});
db.courses.insert({'description': 'See description under subject 15.093[J].', 'title': '6.255[J] Optimization Methods', 'total_units': 12, 'course_numbers': ['6.255[J]'], 'units': '4-0-8', 'name': 'Optimization Methods'});
db.courses.insert({'description': 'Theory and computational techniques for optimization problems involving polynomial equations and inequalities with particular, emphasis on the connections with semidefinite optimization. Develops algebraic and numerical approaches of general applicability, with a view towards methods that simultaneously incorporate both elements, stressing convexity-based ideas, complexity results, and efficient implementations. Examples from several engineering areas, in particular systems and control applications. Topics include semidefinite programming, resultants/discriminants, hyperbolic polynomials, Groebner bases, quantifier elimination, and sum of squares.', 'title': '6.256 Algebraic Techniques and Semidefinite Optimization', 'total_units': 12, 'course_numbers': ['6.256'], 'units': '3-0-9', 'name': 'Algebraic Techniques and Semidefinite Optimization'});
db.courses.insert({'description': 'Advanced study of topics in communications. Specific focus varies from year to year.', 'title': '6.260, 6.261 Advanced Topics in Communications', 'total_units': 12, 'course_numbers': ['6.260,'], 'units': 'Can be repeated for credit.', 'name': '6.261 Advanced Topics in Communications'});
db.courses.insert({'description': 'Review of probability and laws of large numbers; Poisson counting process and renewal processes; Markov chains (including Markov decision theory), branching processes, birth-death processes, and semi-Markov processes; continuous-time Markov chains and reversibility; random walks, martingales, and large deviations; applications from queueing, communication, control, and operations research.', 'title': '6.262 Discrete Stochastic Processes', 'total_units': 12, 'course_numbers': ['6.262'], 'units': '4-0-8', 'name': 'Discrete Stochastic Processes'});
db.courses.insert({'description': 'Provides an introduction to data networks with an analytic perspective, using telephone networks, wireless networks, optical networks, the Internet and data centers as primary applications. Presents basic tools for modeling and performance analysis accompanied by elementary, meaningful simulations. Develops insights for large networks by means of simple approximations. Draws upon concepts from queueing theory and optimization.', 'title': '6.263[J] Data-Communication Networks', 'total_units': 12, 'course_numbers': ['6.263[J]'], 'units': '3-0-9', 'name': 'Data-Communication Networks'});
db.courses.insert({'description': 'See description under subject 15.070[J].', 'title': '6.265[J] Advanced Stochastic Processes', 'total_units': 12, 'course_numbers': ['6.265[J]'], 'units': '3-0-9', 'name': 'Advanced Stochastic Processes'});
db.courses.insert({'description': 'Introduction to modern heterogeneous networks and the provision of heterogeneous services. Architectural principles, analysis, algorithmic techniques, performance analysis, and existing designs are developed and applied to understand current problems in network design and architecture. Begins with basic principles of networking. Emphasizes development of mathematical and algorithmic tools; applies them to understanding network layer design from the performance and scalability viewpoint. Concludes with network management and control, including the architecture and performance analysis of interconnected heterogeneous networks. Provides background and insight to understand current network literature and to perform research on networks with the aid of network design projects.', 'title': '6.267 Heterogeneous Networks: Architecture, Transport, Proctocols, and Management', 'total_units': 12, 'course_numbers': ['6.267'], 'units': '4-0-8', 'name': 'Heterogeneous Networks: Architecture, Transport, Proctocols, and Management'});
db.courses.insert({'description': 'Introduces the main mathematical models used to describe large networks and dynamical processes that evolve on networks. Static models of random graphs, preferential attachment, and other graph evolution models. Epidemic propagation, opinion dynamics, social learning, and inference in networks. Applications drawn from social, economic, natural, and infrastructure networks, as well as networked decision systems such as sensor networks.', 'title': '6.268 Network Science and Models', 'total_units': 12, 'course_numbers': ['6.268'], 'units': '3-0-9', 'name': 'Network Science and Models'});
db.courses.insert({'description': 'Analysis and design of transistor circuits, based directly on the semiconductor physics and transistor circuit models developed in 6.012. High-frequency and low-frequency design calculations and simulation of multistage transistor circuits. Trans-linear circuits. Introduction to operational-amplifier design and application. Some previous laboratory experience assumed.', 'title': '6.301 Solid-State Circuits', 'total_units': 12, 'course_numbers': ['6.301'], 'units': '3-2-7', 'name': 'Solid-State Circuits'});
db.courses.insert({'description': 'Learn-by-design introduction to continuous and discrete-time system modeling and feedback control. Topics include performance metrics; time- and frequency-domain model extraction and classical control; and basic state-space control. Students apply the control concepts in weekly labs and in a midterm project. Labs involve designing circuits and software, and using sensors and a high-performance microcontroller, to address control problems, such as positioning a motor- or propeller-actuated robot arm, reducing distortion in a PWM-based audio amplifier, eliminating field crosstalk for a magnetic-resonance imager, stabilizing magnetic levitation, balancing a two-wheel vehicle. Students taking graduate version complete additional assignments and an extra lab on observer-based state-space control. Intended for students who have previous laboratory experience with electronic systems.', 'title': '6.302 Feedback System Design', 'total_units': 12, 'course_numbers': ['6.302'], 'units': '4-2-6', 'name': 'Feedback System Design'});
db.courses.insert({'description': 'Learn-by-design introduction to continuous and discrete-time system modeling and feedback control. Topics include performance metrics; time- and frequency-domain model extraction and classical control; and basic state-space control. Students apply the control concepts in weekly labs and in a midterm project. Labs involve designing circuits and software, and using sensors and a high-performance microcontroller, to address control problems, such as positioning a motor- or propeller-actuated robot arm, reducing distortion in a PWM-based audio amplifier, eliminating field crosstalk for a magnetic-resonance imager, stabilizing magnetic levitation, balancing a two-wheel vehicle. Students taking graduate version complete additional assignments and an extra lab on observer-based state-space control. Intended for students who have previous laboratory experience with electronic systems. students taking graduate version complete additional assignments.', 'title': '6.320 Feedback System Design (New)', 'total_units': 12, 'course_numbers': ['6.320'], 'units': '4-2-6', 'name': 'Feedback System Design (New)'});
db.courses.insert({'description': 'Advanced study of topics in circuits. Specific focus varies from year to year. Consult department for details.', 'title': '6.332, 6.333 Advanced Topics in Circuits', 'total_units': 12, 'course_numbers': ['6.332,'], 'units': 'Can be repeated for credit.', 'name': '6.333 Advanced Topics in Circuits'});
db.courses.insert({'description': 'The application of electronics to energy conversion and control. Modeling, analysis, and control techniques. Design of power circuits including inverters, rectifiers, and dc-dc converters. Analysis and design of magnetic components and filters. Characteristics of power semiconductor devices. Numerous application examples, such as motion control systems, power supplies, and radio-frequency power amplifiers.', 'title': '6.334 Power Electronics', 'total_units': 12, 'course_numbers': ['6.334'], 'units': '3-0-9', 'name': 'Power Electronics'});
db.courses.insert({'description': 'See description under subject 18.336[J].', 'title': '6.335[J] Fast Methods for Partial Differential and Integral Equations', 'total_units': 12, 'course_numbers': ['6.335[J]'], 'units': '3-0-9', 'name': 'Fast Methods for Partial Differential and Integral Equations'});
db.courses.insert({'description': "Introduction to computational techniques for the simulation of a large variety of engineering and physical systems. Applications are drawn from aerospace, mechanical, electrical, chemical engineering, biology, and materials science. Topics include mathematical formulations (techniques for automatic assembly of mathematical problems from physics' principles); sparse, direct and iterative solution techniques for linear systems; Newton and Homotopy methods for nonlinear problems; discretization methods for ordinary, time-periodic and partial differential equations; accelerated methods for integral equations; techniques for automatic generation of compact dynamical system models and model order reduction.", 'title': '6.336[J] Introduction to Numerical Simulation', 'total_units': 12, 'course_numbers': ['6.336[J]'], 'units': '3-3-6', 'name': 'Introduction to Numerical Simulation'});
db.courses.insert({'description': 'See description under subject 18.335[J].', 'title': '6.337[J] Introduction to Numerical Methods', 'total_units': 12, 'course_numbers': ['6.337[J]'], 'units': '3-0-9', 'name': 'Introduction to Numerical Methods'});
db.courses.insert({'description': 'See description under subject 18.337[J].', 'title': '6.338[J] Parallel Computing', 'total_units': 12, 'course_numbers': ['6.338[J]'], 'units': '3-0-9', 'name': 'Parallel Computing'});
db.courses.insert({'description': 'See description under subject 16.920[J].', 'title': '6.339[J] Numerical Methods for Partial Differential Equations', 'total_units': 12, 'course_numbers': ['6.339[J]'], 'units': '3-0-9', 'name': 'Numerical Methods for Partial Differential Equations'});
db.courses.insert({'description': 'Representation, analysis, and design of discrete time signals and systems. Decimation, interpolation, and sampling rate conversion. Noise shaping. Flowgraph structures for DT systems. Lattice filters. Time- and frequency-domain design techniques for IIR and FIR filters. Parametric signal modeling, linear prediction, and the relation to lattice filters. Discrete Fourier transform (DFT). Computation of the DFT including FFT algorithms. Short-time Fourier analysis and relation to filter banks. Multirate techniques. Perfect reconstruction filter banks and their relation to wavelets.', 'title': '6.341 Discrete-Time Signal Processing', 'total_units': 12, 'course_numbers': ['6.341'], 'units': '4-0-8', 'name': 'Discrete-Time Signal Processing'});
db.courses.insert({'description': 'Digital images as two-dimensional signals. Digital signal processing theories used for digital image processing, including one-dimensional and two-dimensional convolution, Fourier transform, discrete Fourier transform, and discrete cosine transform. Image processing basics. Image enhancement. Image restoration. Image coding and compression. Video processing including video coding and compression. Additional topics including digital high-definition television systems.', 'title': '6.344 Digital Image Processing', 'total_units': 12, 'course_numbers': ['6.344'], 'units': '3-0-9', 'name': 'Digital Image Processing'});
db.courses.insert({'description': 'Introduces the rapidly developing fields of automatic speech recognition and spoken language processing. Topics include acoustic theory of speech production, acoustic-phonetics, signal representation, acoustic and language modeling, search, hidden Markov modeling, deep neural networks, system robustness, adaptation, and other related speech processing topics. Lecture material intersperses theory with practice. Includes problem sets, laboratory exercises, and opened-ended term project.', 'title': '6.345[J] Automatic Speech Recognition', 'total_units': 12, 'course_numbers': ['6.345[J]'], 'units': '3-1-8', 'name': 'Automatic Speech Recognition'});
db.courses.insert({'description': 'Advanced study of topics in signals and systems. Specific focus varies from year to year.', 'title': '6.347, 6.348 Advanced Topics in Signals and Systems', 'total_units': 12, 'course_numbers': ['6.347,'], 'units': 'Can be repeated for credit.', 'name': '6.348 Advanced Topics in Signals and Systems'});
db.courses.insert({'description': 'Device and circuit level optimization of digital building blocks. MOS device models including Deep Sub-Micron effects. Circuit design styles for logic, arithmetic, and sequential blocks. Estimation and minimization of energy consumption. Interconnect models and parasitics, device sizing and logical effort, timing issues (clock skew and jitter), and active clock distribution techniques. Memory architectures, circuits (sense amplifiers), and devices. Testing of integrated circuits. Extensive custom and standard cell layout and simulation in design projects and software labs.', 'title': '6.374 Analysis and Design of Digital Integrated Circuits', 'total_units': 12, 'course_numbers': ['6.374'], 'units': '3-3-6', 'name': 'Analysis and Design of Digital Integrated Circuits'});
db.courses.insert({'description': 'Introduction to the design and implementation of large-scale digital systems using hardware description languages and high-level synthesis tools in conjunction with standard commercial electronic design automation (EDA) tools. Emphasizes modular and robust designs, reusable modules, correctness by construction, architectural exploration, meeting area and timing constraints, and developing functional field-programmable gate array (FPGA) prototypes. Extensive use of CAD tools in weekly labs serve as preparation for a multi-person design project on multi-million gate FPGAs.', 'title': '6.375 Complex Digital Systems Design', 'total_units': 12, 'course_numbers': ['6.375'], 'units': '5-5-2', 'name': 'Complex Digital Systems Design'});
db.courses.insert({'description': 'Provides an introduction to probability theory and the modeling and analysis of probabilistic systems. Probabilistic models, conditional probability. Discrete and continuous random variables. Expectation and conditional expectation. Limit Theorems. Students taking graduate version complete additional assignments.', 'title': '6.431A Introduction to Probability I (New)', 'total_units': 6, 'course_numbers': ['6.431A'], 'units': '2-0-4', 'name': 'Introduction to Probability I (New)'});
db.courses.insert({'description': 'Further topics in probability. Bayesian estimation and hypothesis testing. Elements of statistical inference. Bernoulli and Poisson processes. Markov chains. Students taking graduate version complete additional assignments.', 'title': '6.431B Introduction to Probability II (New)', 'total_units': 6, 'course_numbers': ['6.431B'], 'units': '2-0-4', 'name': 'Introduction to Probability II (New)'});
db.courses.insert({'description': 'Rigorous introduction to fundamentals of statistics motivated by engineering applications. Topics include exponential families, order statistics, sufficient statistics, estimation theory, hypothesis testing, measures of performance, notions of optimality, analysis of variance (ANOVA), simple linear regression, and selected topics.', 'title': '6.434[J] Statistics for Engineers and Scientists', 'total_units': 12, 'course_numbers': ['6.434[J]'], 'units': '3-0-9', 'name': 'Statistics for Engineers and Scientists'});
db.courses.insert({'description': 'Introduction to probability theory. Probability spaces and measures. Discrete and continuous random variables. Conditioning and independence. Multivariate normal distribution. Abstract integration, expectation, and related convergence results. Moment generating and characteristic functions. Bernoulli and Poisson process. Finite-state Markov chains. Convergence notions and their relations. Limit theorems. Familiarity with elementary notions in probability and real analysis is desirable.', 'title': '6.436[J] Fundamentals of Probability', 'total_units': 12, 'course_numbers': ['6.436[J]'], 'units': '4-0-8', 'name': 'Fundamentals of Probability'});
db.courses.insert({'description': 'Introduction to principles of Bayesian and non-Bayesian statistical inference. Hypothesis testing and parameter estimation, sufficient statistics; exponential families. EM agorithm. Log-loss inference criterion, entropy and model capacity. Kullback-Leibler distance and information geometry. Asymptotic analysis and large deviations theory. Model order estimation; nonparametric statistics. Computational issues and approximation techniques; Monte Carlo methods. Selected special topics such as universal prediction and compression.', 'title': '6.437 Inference and Information', 'total_units': 12, 'course_numbers': ['6.437'], 'units': '4-0-8', 'name': 'Inference and Information'});
db.courses.insert({'description': 'Introduction to statistical inference with probabilistic graphical models. Directed and undirected graphical models, and factor graphs, over discrete and Gaussian distributions; hidden Markov models, linear dynamical systems. Sum-product and junction tree algorithms; forward-backward algorithm, Kalman filtering and smoothing. Min-sum and Viterbi algorithms. Variational methods, mean-field theory, and loopy belief propagation. Particle methods and filtering. Building graphical models from data, including parameter estimation and structure learning; Baum-Welch and Chow-Liu algorithms. Selected special topics.', 'title': '6.438 Algorithms for Inference', 'total_units': 12, 'course_numbers': ['6.438'], 'units': '4-0-8', 'name': 'Algorithms for Inference'});
db.courses.insert({'description': 'Introduces the theory of error-correcting codes. Focuses on the essential results in the area, taught from first principles. Special focus on results of asymptotic or algorithmic significance. Principal topics include construction and existence results for error-correcting codes; limitations on the combinatorial performance of error-correcting codes; decoding algorithms; and applications to other areas of mathematics and computer science.', 'title': '6.440 Essential Coding Theory', 'total_units': 12, 'course_numbers': ['6.440'], 'units': '3-0-9', 'name': 'Essential Coding Theory'});
db.courses.insert({'description': 'Mathematical definitions of information measures, convexity, continuity, and variational properties. Lossless source coding; variable-length and block compression; Slepian-Wolf theorem; ergodic sources and Shannon-McMillan theorem. Hypothesis testing, large deviations and I-projection. Fundamental limits of block coding for noisy channels: capacity, dispersion, finite blocklength bounds. Coding with feedback. Joint source-channel problem. Rate-distortion theory, vector quantizers. Advanced topics include Gelfand-Pinsker problem, multiple access channels, broadcast channels (depending on available time).', 'title': '6.441 Information Theory', 'total_units': 12, 'course_numbers': ['6.441'], 'units': '3-0-9', 'name': 'Information Theory'});
db.courses.insert({'description': 'Introduces the fundamental and practical aspects of optical network technology, architecture, design and analysis tools and techniques. The treatment of optical networks are from the architecture and system design points of view. Optical hardware technologies are introduced and characterized as fundamental network building blocks on which optical transmission systems and network architectures are based. Beyond the Physical Layer, the higher network layers (Media Access Control, Network and Transport Layers) are treated together as integral parts of network design. Performance metrics, analysis and optimization techniques are developed to help guide the creation of high performance complex optical networks.', 'title': '6.442 Optical Networks', 'total_units': 12, 'course_numbers': ['6.442'], 'units': '3-0-9', 'name': 'Optical Networks'});
db.courses.insert({'description': 'See description under subject 8.371[J].', 'title': '6.443[J] Quantum Information Science', 'total_units': 12, 'course_numbers': ['6.443[J]'], 'units': '3-0-9', 'name': 'Quantum Information Science'});
db.courses.insert({'description': 'Communication sources and channels; data compression; entropy and the AEP; Lempel-Ziv universal coding; scalar and vector quantization; L2 waveforms; signal space and its representation by sampling and other expansions; aliasing; the Nyquist criterion; PAM and QAM modulation; Gaussian noise and random processes; detection and optimal receivers; fading channels and wireless communication; introduction to communication system design.', 'title': '6.450 Principles of Digital Communication', 'total_units': 12, 'course_numbers': ['6.450'], 'units': '3-0-9', 'name': 'Principles of Digital Communication'});
db.courses.insert({'description': 'Introduction to design, analysis, and fundamental limits of wireless transmission systems. Wireless channel and system models; fading and diversity; resource management and power control; multiple-antenna and MIMO systems; space-time codes and decoding algorithms; multiple-access techniques and multiuser detection; broadcast codes and precoding; cellular and ad-hoc network topologies; OFDM and ultrawideband systems; architectural issues.', 'title': '6.452 Principles of Wireless Communication', 'total_units': 12, 'course_numbers': ['6.452'], 'units': '3-0-9', 'name': 'Principles of Wireless Communication'});
db.courses.insert({'description': 'Quantum optics: Dirac notation quantum mechanics; harmonic oscillator quantization; number states, coherent states, and squeezed states; radiation field quantization and quantum field propagation; P-representation and classical fields. Linear loss and linear amplification: commutator preservation and the Uncertainty Principle; beam splitters; phase-insensitive and phase-sensitive amplifiers. Quantum photodetection: direct detection, heterodyne detection, and homodyne detection. Second-order nonlinear optics: phasematched interactions; optical parametric amplifiers; generation of squeezed states, photon-twin beams, non-classical fourth-order interference, and polarization entanglement. Quantum systems theory: optimum binary detection; quantum precision measurements; quantum cryptography; and quantum teleportation.', 'title': '6.453 Quantum Optical Communication', 'total_units': 12, 'course_numbers': ['6.453'], 'units': '3-0-9', 'name': 'Quantum Optical Communication'});
db.courses.insert({'description': 'Student-run advanced graduate seminar with focus on topics in communications, control, signal processing, optimization. Participants give presentations outside of their own research to expose colleagues to topics not covered in the usual curriculum. Recent topics have included compressed sensing, MDL principle, communication complexity, linear programming decoding, biology in EECS, distributed hypothesis testing, algorithms for random satisfaction problems, and cryptogaphy. Open to advanced students from all areas of EECS.', 'title': '6.454 Graduate Seminar in Area I', 'total_units': 6, 'course_numbers': ['6.454'], 'units': 'Can be repeated for credit.', 'name': 'Graduate Seminar in Area I'});
db.courses.insert({'description': 'Adaptive and non-adaptive processing of signals received at arrays of sensors. Deterministic beamforming, space-time random processes, optimal and adaptive algorithms, and the sensitivity of algorithm performance to modeling errors and limited data. Methods of improving the robustness of algorithms to modeling errors and limited data are derived. Advanced topics include an introduction to matched field processing and physics-based methods of estimating signal statistics. Homework exercises providing the opportunity to implement and analyze the performance of algorithms in processing data supplied during the course.', 'title': '6.456 Array Processing', 'total_units': 12, 'course_numbers': ['6.456'], 'units': '3-2-7', 'name': 'Array Processing'});
db.courses.insert({'description': 'Illustrates computational approaches to solving problems in systems biology. Uses a series of case studies to demonstrate how an effective match between the statement of a biological problem and the selection of an appropriate algorithm or computational technique can lead to fundamental advances. Covers several discrete and numerical algorithms used in simulation, feature extraction, and optimization for molecular, network, and systems models in biology. Students taking graduate version complete additional assignments.', 'title': '6.503 Foundations of Algorithms and Computational Techniques in Systems Biology', 'total_units': 12, 'course_numbers': ['6.503'], 'units': '3-0-9', 'name': 'Foundations of Algorithms and Computational Techniques in Systems Biology'});
db.courses.insert({'description': 'Meets with undergraduate subject 6.021[J]. Requires the completion of more advanced home problems and/or an additional project.', 'title': '6.521[J] Cellular Neurophysiology', 'total_units': 12, 'course_numbers': ['6.521[J]'], 'units': '5-2-5', 'name': 'Cellular Neurophysiology'});
db.courses.insert({'description': 'Application of the principles of energy and mass flow to major human organ systems. Mechanisms of regulation and homeostasis. Anatomical, physiological and pathophysiological features of the cardiovascular, respiratory and renal systems. Systems, features and devices that are most illuminated by the methods of physical sciences. Laboratory work includes some animal studies. Students taking graduate version complete additional assignments.', 'title': '6.522[J] Quantitative Physiology: Organ Transport Systems', 'total_units': 12, 'course_numbers': ['6.522[J]'], 'units': '4-2-6', 'name': 'Quantitative Physiology: Organ Transport Systems'});
db.courses.insert({'description': 'See description under subject 20.410[J].', 'title': '6.524[J] Molecular, Cellular, and Tissue Biomechanics', 'total_units': 12, 'course_numbers': ['6.524[J]'], 'units': '3-0-9', 'name': 'Molecular, Cellular, and Tissue Biomechanics'});
db.courses.insert({'description': 'See description under subject 2.75[J].', 'title': '6.525[J] Medical Device Design', 'total_units': 12, 'course_numbers': ['6.525[J]'], 'units': '3-0-9', 'name': 'Medical Device Design'});
db.courses.insert({'description': 'Experimental investigations of speech processes. Topics: measurement of articulatory movements; measurements of pressures and airflows in speech production; computer-aided waveform analysis and spectral analysis of speech; synthesis of speech; perception and discrimination of speechlike sounds; speech prosody; models for speech recognition; speech development; and other topics. Recommended prerequisites: 6.002 or 18.03.', 'title': '6.542[J] Laboratory on the Physiology, Acoustics, and Perception of Speech', 'total_units': 12, 'course_numbers': ['6.542[J]'], 'units': '2-2-8', 'name': 'Laboratory on the Physiology, Acoustics, and Perception of Speech'});
db.courses.insert({'description': 'Advanced study of topics in BioEECS. Specific focus varies from year to year. Consult department for details.', 'title': '6.544, 6.545 Advanced Topics in BioEECS', 'total_units': 12, 'course_numbers': ['6.544,'], 'units': 'Can be repeated for credit.', 'name': '6.545 Advanced Topics in BioEECS'});
db.courses.insert({'description': 'Studies information processing performance of the human auditory system in relation to current physiological knowledge. Examines mathematical models for the quantification of auditory-based behavior and the relation between behavior and peripheral physiology, reflecting the tono-topic organization and stochastic responses of the auditory system. Mathematical models of psychophysical relations, incorporating quantitative knowledge of physiological transformations by the peripheral auditory system.', 'title': '6.552[J] Signal Processing by the Auditory System: Perception', 'total_units': 12, 'course_numbers': ['6.552[J]'], 'units': '3-0-9', 'name': 'Signal Processing by the Auditory System: Perception'});
db.courses.insert({'description': 'See description under subject HST.582[J].', 'title': '6.555[J] Biomedical Signal and Image Processing', 'total_units': 12, 'course_numbers': ['6.555[J]'], 'units': '3-3-6', 'name': 'Biomedical Signal and Image Processing'});
db.courses.insert({'description': 'Applies analysis of signals and noise in linear systems, sampling, and Fourier properties to magnetic resonance (MR) imaging acquisition and reconstruction. Provides adequate foundation for MR physics to enable study of RF excitation design, efficient Fourier sampling, parallel encoding, reconstruction of non-uniformly sampled data, and the impact of hardware imperfections on reconstruction performance. Surveys active areas of MR research. Assignments include Matlab-based work with real data. Includes visit to a scan site for human MR studies.', 'title': '6.556[J] Data Acquisition and Image Reconstruction in MRI', 'total_units': 12, 'course_numbers': ['6.556[J]'], 'units': '3-0-9', 'name': 'Data Acquisition and Image Reconstruction in MRI'});
db.courses.insert({'description': 'See description under subject 2.18[J].', 'title': '6.557[J] Biomolecular Feedback Systems', 'total_units': 12, 'course_numbers': ['6.557[J]'], 'units': '3-0-9', 'name': 'Biomolecular Feedback Systems'});
db.courses.insert({'description': 'See description under subject 20.430[J].', 'title': '6.561[J] Fields, Forces, and Flows in Biological Systems', 'total_units': 12, 'course_numbers': ['6.561[J]'], 'units': '3-0-9', 'name': 'Fields, Forces, and Flows in Biological Systems'});
db.courses.insert({'description': 'See description under subject 20.305[J].', 'title': '6.580[J] Principles of Synthetic Biology', 'total_units': 12, 'course_numbers': ['6.580[J]'], 'units': '3-0-9', 'name': 'Principles of Synthetic Biology'});
db.courses.insert({'description': 'Illustrates computational approaches to solving problems in systems biology. Uses a series of case studies to demonstrate how an effective match between the statement of a biological problem and the selection of an appropriate algorithm or computational technique can lead to fundamental advances. Covers several discrete and numerical algorithms used in simulation, feature extraction, and optimization for molecular, network, and systems models in biology. Students taking graduate version complete additional assignments.', 'title': '6.581[J] Foundations of Algorithms and Computational Techniques in Systems Biology', 'total_units': 12, 'course_numbers': ['6.581[J]'], 'units': '3-0-9', 'name': 'Foundations of Algorithms and Computational Techniques in Systems Biology'});
db.courses.insert({'description': 'See description under subject 20.405[J].', 'title': '6.589[J] Principles of Synthetic Biology', 'total_units': 12, 'course_numbers': ['6.589[J]'], 'units': '3-0-9', 'name': 'Principles of Synthetic Biology'});
db.courses.insert({'description': "Covers the fundamentals of optics and the interaction of light and matter, leading to devices such as light emitting diodes, optical amplifiers, and lasers. Topics include classical ray, wave, beam, and Fourier optics; Maxwell's electromagnetic waves; resonators; quantum theory of photons; light-matter interaction; laser amplification; lasers; and semiconductors optoelectronics. Students taking graduate version complete additional assignments.", 'title': '6.602 Fundamentals of Photonics', 'total_units': 12, 'course_numbers': ['6.602'], 'units': '3-0-9', 'name': 'Fundamentals of Photonics'});
db.courses.insert({'description': "Covers the fundamentals of optics and the interaction of light and matter, leading to devices such as light emitting diodes, optical amplifiers, and lasers. Topics include classical ray, wave, beam, and Fourier optics; Maxwell's electromagnetic waves; resonators; quantum theory of photons; light-matter interaction; laser amplification; lasers; and semiconductors optoelectronics. Students taking graduate version complete additional assignments.", 'title': '6.621 Fundamentals of Photonics', 'total_units': 12, 'course_numbers': ['6.621'], 'units': '3-0-9', 'name': 'Fundamentals of Photonics'});
db.courses.insert({'description': "Explores electromagnetic phenomena in modern applications, including wireless and optical communications, circuits, computer interconnects and peripherals, microwave communications and radar, antennas, sensors, micro-electromechanical systems, and power generation and transmission. Fundamentals include quasistatic and dynamic solutions to Maxwell's equations; waves, radiation, and diffraction; coupling to media and structures; guided and unguided waves; modal expansions; resonance; acoustic analogs; and forces, power, and energy.", 'title': '6.630 Electromagnetics', 'total_units': 12, 'course_numbers': ['6.630'], 'units': '4-0-8', 'name': 'Electromagnetics'});
db.courses.insert({'description': "Introduction to fundamental concepts and techniques of optics, photonics, and fiber optics. Review of Maxwell's equations, light propagation, and reflection from dielectrics mirrors and filters. Interferometers, filters, and optical imaging systems. Fresnel and Fraunhoffer diffraction theory. Propagation of Gaussian beams and laser resonator design. Optical waveguides and optical fibers. Optical waveguide and photonic devices.", 'title': '6.631 Optics and Photonics', 'total_units': 12, 'course_numbers': ['6.631'], 'units': '3-0-9', 'name': 'Optics and Photonics'});
db.courses.insert({'description': "Solutions to Maxwell equations and physical interpretation. Topics include waves in media, equivalence principle, duality and complementarity, Huygens' principle, Fresnel and Fraunhofer diffraction, radiation and dyadic Green's functions, scattering, metamaterials, and plasmonics, mode theory, dielectric waveguides, and resonators. Examples deal with limiting cases of electromagnetic theory, multi-port elements, filters and antennas. Discusses current topics in microwave and photonic devices.", 'title': '6.632 Electromagnetic Wave Theory', 'total_units': 12, 'course_numbers': ['6.632'], 'units': '3-0-9', 'name': 'Electromagnetic Wave Theory'});
db.courses.insert({'description': 'Techniques of nonlinear optics with emphasis on fundamentals for research and engineering in optics, photonics, and spectroscopy. Electro optic modulators, harmonic generation, and frequency conversion devices. Nonlinear effects in optical fibers including self-phase modulation, nonlinear wave propagation, and solitons. Interaction of light with matter, laser operation, density matrix techniques, nonlinear spectroscopies, and femtosecond optics.', 'title': '6.634[J] Nonlinear Optics', 'total_units': 12, 'course_numbers': ['6.634[J]'], 'units': '3-0-9', 'name': 'Nonlinear Optics'});
db.courses.insert({'description': 'Principles of operation and applications of devices and systems for optical signal generation, transmission, detection, storage, processing and display. Topics include review of the basic properties of electromagnetic waves; coherence and interference; diffraction and holography; Fourier optics; coherent and incoherent imaging and signal processing systems; optical properties of materials; lasers and LEDs; electro-optic and acousto-optic light modulators; photorefractive and liquid-crystal light modulation; spatial light modulators and displays; optical waveguides and fiber-optic communication systems; photodetectors; 2-D and 3-D optical storage technologies; adaptive optical systems; role of optics in next-generation computers. Student research paper on a specific contemporary topic required. Recommended prerequisites: 6.007 or 8.03.', 'title': '6.637 Optical Signals, Devices, and Systems', 'total_units': 12, 'course_numbers': ['6.637'], 'units': '3-0-9', 'name': 'Optical Signals, Devices, and Systems'});
db.courses.insert({'description': "Electric and magnetic quasistatic forms of Maxwell's equations applied to dielectric, conduction, and magnetization boundary value problems. Electromagnetic forces, force densities, and stress tensors, including magnetization and polarization. Thermodynamics of electromagnetic fields, equations of motion, and energy conservation. Applications to synchronous, induction, and commutator machines; sensors and transducers; microelectromechanical systems; propagation and stability of electromechanical waves; and charge transport phenomena.", 'title': '6.641 Electromagnetic Fields, Forces, and Motion', 'total_units': 12, 'course_numbers': ['6.641'], 'units': '4-0-8', 'name': 'Electromagnetic Fields, Forces, and Motion'});
db.courses.insert({'description': 'Laws, approximations, and relations of continuum mechanics. Mechanical and electromechanical transfer relations. Statics and dynamics of electromechanical systems having a static equilibrium. Electromechanical flows. Field coupling with thermal and molecular diffusion. Electrokinetics. Streaming interactions. Application to materials processing, magnetohydrodynamic and electrohydrodynamic pumps and generators, ferrohydrodynamics, physiochemical systems, heat transfer, continuum feedback control, electron beam devices, and plasma dynamics.', 'title': '6.642 Continuum Electromechanics', 'total_units': 12, 'course_numbers': ['6.642'], 'units': '4-0-8', 'name': 'Continuum Electromechanics'});
db.courses.insert({'description': 'Advanced study of topics in applied physics. Specific focus varies from year to year. Consult department for details.', 'title': '6.644, 6.645 Advanced Topics in Applied Physics', 'total_units': 12, 'course_numbers': ['6.644,'], 'units': 'Can be repeated for credit.', 'name': '6.645 Advanced Topics in Applied Physics'});
db.courses.insert({'description': "Treatment of electromechanical transducers, rotating and linear electric machines. Lumped-parameter electromechanics. Power flow using Poynting's theorem, force estimation using the Maxwell stress tensor and Principle of virtual work. Development of analytical techniques for predicting device characteristics: energy conversion density, efficiency; and of system interaction characteristics: regulation, stability, controllability, and response. Use of electric machines in drive systems. Problems taken from current research.", 'title': '6.685 Electric Machines', 'total_units': 12, 'course_numbers': ['6.685'], 'units': '3-0-9', 'name': 'Electric Machines'});
db.courses.insert({'description': 'Electric circuit theory with application to power handling electric circuits. Modeling and behavior of electromechanical devices, including magnetic circuits, motors, and generators. Operational fundamentals of synchronous, induction and DC machinery. Interconnection of generators and motors with electric power transmission and distribution circuits. Power generation, including alternative and sustainable sources. Students taking graduate version complete additional assignments.', 'title': '6.690 Introduction to Electric Power Systems', 'total_units': 12, 'course_numbers': ['6.690'], 'units': '3-0-9', 'name': 'Introduction to Electric Power Systems'});
db.courses.insert({'description': 'See description under subject IDS.505[J].', 'title': '6.695[J] Engineering, Economics and Regulation of the Electric Power Sector', 'total_units': 12, 'course_numbers': ['6.695[J]'], 'units': '3-0-9', 'name': 'Engineering, Economics and Regulation of the Electric Power Sector'});
db.courses.insert({'description': "Transistors at the nanoscale. Quantization, wavefunctions, and Schrodinger's equation. Introduction to electronic properties of molecules, carbon nanotubes, and crystals. Energy band formation and the origin of metals, insulators and semiconductors. Ballistic transport, Ohm's law, ballistic versus traditional MOSFETs, fundamental limits to computation.", 'title': '6.701 Introduction to Nanoelectronics', 'total_units': 12, 'course_numbers': ['6.701'], 'units': '4-0-8', 'name': 'Introduction to Nanoelectronics'});
db.courses.insert({'description': 'Provides an introduction to microsystem design. Covers material properties, microfabrication technologies, structural behavior, sensing methods, electromechanical actuation, thermal actuation and control, multi-domain modeling, noise, and microsystem packaging. Applies microsystem modeling, and manufacturing principles to the design and analysis a variety of microscale sensors and actuators (e.g., optical MEMS, bioMEMS, and inertial sensors). Emphasizes modeling and simulation in the design process. Students taking the graduate version complete additional assignments.', 'title': '6.717[J] Design and Fabrication of Microelectromechanical Systems', 'total_units': 12, 'course_numbers': ['6.717[J]'], 'units': '3-0-9', 'name': 'Design and Fabrication of Microelectromechanical Systems'});
db.courses.insert({'description': 'Meets with undergraduate subject 6.701, but requires the completion of additional/different homework assignments and or projects. See subject description under 6.701.', 'title': '6.719 Nanoelectronics', 'total_units': 12, 'course_numbers': ['6.719'], 'units': '4-0-8', 'name': 'Nanoelectronics'});
db.courses.insert({'description': 'Covers physics of microelectronic semiconductor devices for integrated circuit applications. Topics include semiconductor fundamentals, p-n junction, metal-oxide semiconductor structure, metal-semiconductor junction, MOS field-effect transistor, and bipolar junction transistor. Studies modern nanoscale devices, including electrostatic scaling, materials beyond Si, carrier transport from the diffusive to the ballistic regime. Emphasizes physical understanding of device operation through energy band diagrams and short-channel MOSFET device design. Includes device modeling exercises. Familiarity with MATLAB required. 2 Engineering Design Points.', 'title': '6.720[J] Integrated Microelectronic Devices', 'total_units': 12, 'course_numbers': ['6.720[J]'], 'units': '4-0-8', 'name': 'Integrated Microelectronic Devices'});
db.courses.insert({'description': "Elementary quantum mechanics and statistical physics. Introduces applied quantum physics. Emphasizes experimental basis for quantum mechanics. Applies Schrodinger's equation to the free particle, tunneling, the harmonic oscillator, and hydrogen atom. Variational methods. Elementary statistical physics; Fermi-Dirac, Bose-Einstein, and Boltzmann distribution functions. Simple models for metals, semiconductors, and devices such as electron microscopes, scanning tunneling microscope, thermonic emitters, atomic force microscope, and more.", 'title': '6.728 Applied Quantum and Statistical Physics', 'total_units': 12, 'course_numbers': ['6.728'], 'units': '4-0-8', 'name': 'Applied Quantum and Statistical Physics'});
db.courses.insert({'description': 'Classical and quantum models of electrons and lattice vibrations in solids, emphasizing physical models for elastic properties, electronic transport, and heat capacity. Crystal lattices, electronic energy band structures, phonon dispersion relations, effective mass theorem, semiclassical equations of motion, electron scattering and semiconductor optical properties. Band structure and transport properties of selected semiconductors. Connection of quantum theory of solids with quasi-Fermi levels and Boltzmann transport used in device modeling.', 'title': '6.730 Physics for Solid-State Applications', 'total_units': 12, 'course_numbers': ['6.730'], 'units': '5-0-7', 'name': 'Physics for Solid-State Applications'});
db.courses.insert({'description': 'Focuses on the physics of the interaction of photons with semiconductor materials. Uses the band theory of solids to calculate the absorption and gain of semiconductor media; and uses rate equation formalism to develop the concepts of laser threshold, population inversion, and modulation response. Presents theory and design for photodetectors, solar cells, modulators, amplifiers, and lasers. Introduces noise models for semiconductor devices, and applications of optoelectronic devices to fiber optic communications.', 'title': '6.731 Semiconductor Optoelectronics: Theory and Design', 'total_units': 12, 'course_numbers': ['6.731'], 'units': '3-0-9', 'name': 'Semiconductor Optoelectronics: Theory and Design'});
db.courses.insert({'description': 'Continuation of 6.730 emphasizing applications-related physical issues in solids. Topics include: electronic structure and energy band diagrams of semiconductors, metals, and insulators; Fermi surfaces; dynamics of electrons under electric and magnetic fields; classical diffusive transport phenomena such as electrical and thermal conduction and thermoelectric phenomena; quantum transport in tunneling and ballistic devices; optical properties of metals, semiconductors, and insulators; impurities and excitons; photon-lattice interactions; Kramers-Kronig relations; optoelectronic devices based on interband and intersubband transitions; magnetic properties of solids; exchange energy and magnetic ordering; magneto-oscillatory phenomena; quantum Hall effect; superconducting phenomena and simple models.', 'title': '6.732 Physics of Solids', 'total_units': 12, 'course_numbers': ['6.732'], 'units': '4-0-8', 'name': 'Physics of Solids'});
db.courses.insert({'description': 'Advanced study of topics in materials, devices, and nanotechnology. Specific focus varies from year to year.', 'title': '6.735, 6.736 Advanced Topics in Materials, Devices, and Nanotechnology', 'total_units': 12, 'course_numbers': ['6.735,'], 'units': 'Can be repeated for credit.', 'name': '6.736 Advanced Topics in Materials, Devices, and Nanotechnology'});
db.courses.insert({'description': 'Presents advanced physical models and practical aspects of front-end microfabrication processes, such as oxidation, diffusion, ion implantation, chemical vapor deposition, atomic layer deposition, etching, and epitaxy. Covers topics relevant to CMOS, bipolar, and optoelectronic device fabrication, including high k gate dielectrics, gate etching, implant-damage enhanced diffusion, advanced metrology, stress effects on oxidation, non-planar and nanowire device fabrication, SiGe and fabrication of process-induced strained Si. Exposure to CMOS process integration concepts, and impacts of processing on device characteristics. Students use modern process simulation tools.', 'title': '6.774 Physics of Microfabrication: Front End Processing', 'total_units': 12, 'course_numbers': ['6.774'], 'units': '3-0-9', 'name': 'Physics of Microfabrication: Front End Processing'});
db.courses.insert({'description': 'A detailed exposition of the principles involved in designing and optimizing analog and mixed-signal circuits in CMOS technologies. Small-signal and large-signal models. Systemic methodology for device sizing and biasing. Basic circuit building blocks. Operational amplifier design. Large signal considerations. Principles of switched capacitor networks including switched-capacitor and continuous-time integrated filters. Basic and advanced A/D and D/A converters, delta-sigma modulators, RF and other signal processing circuits. Design projects on op amps and subsystems are a required part of the subject. 4 Engineering Design Points.', 'title': '6.775 CMOS Analog and Mixed-Signal Circuit Design', 'total_units': 12, 'course_numbers': ['6.775'], 'units': '3-0-9', 'name': 'CMOS Analog and Mixed-Signal Circuit Design'});
db.courses.insert({'description': 'Principles and techniques of high-speed integrated circuits used in wireless/wireline data links and remote sensing. On-chip passive component design of inductors, capacitors, and antennas. Analysis of distributed effects, such as transmission line modeling, S-parameters, and Smith chart. Transceiver architectures and circuit blocks, which include low-noise amplifiers, mixers, voltage-controlled oscillators, power amplifiers, and frequency dividers. Involves IC/EM simulation and laboratory projects.', 'title': '6.776 High Speed Communication Circuits', 'total_units': 12, 'course_numbers': ['6.776'], 'units': '3-3-6', 'name': 'High Speed Communication Circuits'});
db.courses.insert({'description': 'Provides an introduction to microsystem design. Covers material properties, microfabrication technologies, structural behavior, sensing methods, electromechanical actuation, thermal actuation and control, multi-domain modeling, noise, and microsystem packaging. Applies microsystem modeling, and manufacturing principles to the design and analysis a variety of microscale sensors and actuators (e.g., optical MEMS, bioMEMS, and inertial sensors). Emphasizes modeling and simulation in the design process. Students taking the graduate version complete additional assignments. 4 Engineering Design Points.', 'title': '6.777[J] Design and Fabrication of Microelectromechanical Systems', 'total_units': 12, 'course_numbers': ['6.777[J]'], 'units': '3-0-9', 'name': 'Design and Fabrication of Microelectromechanical Systems'});
db.courses.insert({'description': 'See description under subject 2.830[J].', 'title': '6.780[J] Control of Manufacturing Processes', 'total_units': 12, 'course_numbers': ['6.780[J]'], 'units': '3-0-9', 'name': 'Control of Manufacturing Processes'});
db.courses.insert({'description': 'Describes current techniques used to analyze and fabricate nanometer-length-scale structures and devices. Emphasizes imaging and patterning of nanostructures, including fundamentals of optical, electron (scanning, transmission, and tunneling), and atomic-force microscopy; optical, electron, ion, and nanoimprint lithography, templated self-assembly, and resist technology. Surveys substrate characterization and preparation, facilities, and metrology requirements for nanolithography. Addresses nanodevice processing methods, such as liquid and plasma etching, lift-off, electroplating, and ion-implant. Discusses applications in nanoelectronics, nanomaterials, and nanophotonics.', 'title': '6.781[J] Nanostructure Fabrication', 'total_units': 12, 'course_numbers': ['6.781[J]'], 'units': '4-0-8', 'name': 'Nanostructure Fabrication'});
db.courses.insert({'description': 'Deriving a symbolic description of the environment from an image. Understanding physics of image formation. Image analysis as an inversion problem. Binary image processing and filtering of images as preprocessing steps. Recovering shape, lightness, orientation, and motion. Using constraints to reduce the ambiguity. Photometric stereo and extended Gaussian sphere. Applications to robotics; intelligent interaction of machines with their environment. Students taking the graduate version complete different assignments.', 'title': '6.801 Machine Vision', 'total_units': 12, 'course_numbers': ['6.801'], 'units': '3-0-9', 'name': 'Machine Vision'});
db.courses.insert({'description': 'Provides an introduction to computational and systems biology. Includes units on the analysis of protein and nucleic acid sequences, protein structures, and biological networks. Presents principles and methods used for sequence alignment, motif finding, expression array analysis, structural modeling, structure design and prediction, and network analysis and modeling. Techniques include dynamic programming, Markov and hidden Markov models, Bayesian networks, clustering methods, and energy minimization approaches. Exposes students to emerging research areas. Designed for students with strong backgrounds in either molecular biology or computer science. Some foundational material covering basic programming skills, probability and statistics is provided for students with less quantitative backgrounds. Students taking graduate version complete additional assignments.', 'title': '6.802[J] Foundations of Computational and Systems Biology', 'total_units': 12, 'course_numbers': ['6.802[J]'], 'units': '3-0-9', 'name': 'Foundations of Computational and Systems Biology'});
db.courses.insert({'description': 'Analyzes seminal work directed at the development of a computational understanding of human intelligence, such as work on learning, language, vision, event representation, commonsense reasoning, self reflection, story understanding, and analogy. Reviews visionary ideas of Turing, Minsky, and other influential thinkers. Examines the implications of work on brain scanning, developmental psychology, and cognitive psychology. Emphasis on discussion and analysis of original papers. Students taking graduate version complete additional assignments.', 'title': '6.803 The Human Intelligence Enterprise', 'total_units': 12, 'course_numbers': ['6.803'], 'units': '3-0-9', 'name': 'The Human Intelligence Enterprise'});
db.courses.insert({'description': 'See description under subject 9.66[J].', 'title': '6.804[J] Computational Cognitive Science', 'total_units': 12, 'course_numbers': ['6.804[J]'], 'units': '3-0-9', 'name': 'Computational Cognitive Science'});
db.courses.insert({'name': 'Foundations of Information Policy', 'tags': ['HASS'], 'title': '6.805[J] Foundations of Information Policy', 'total_units': 12, 'course_numbers': ['6.805[J]'], 'units': '3-0-9', 'description': 'Studies the growth of computer and communications technology and the new legal and ethical challenges that reflect tensions between individual rights and societal needs. Topics include computer crime; intellectual property restrictions on software; encryption, privacy, and national security; academic freedom and free speech. Students meet and question technologists, activists, law enforcement agents, journalists, and legal experts. Instruction and practice in oral and written communication provided. Students taking graduate version complete additional assignments.'});
db.courses.insert({'description': 'Introduces the study of human language from a computational perspective, including syntactic, semantic and discourse processing models. Emphasizes machine learning methods and algorithms. Uses these methods and models in applications such as syntactic parsing, information extraction, statistical machine translation, dialogue systems, and summarization. Students taking graduate version complete additional assignments.', 'title': '6.806 Advanced Natural Language Processing', 'total_units': 12, 'course_numbers': ['6.806'], 'units': '3-0-9', 'name': 'Advanced Natural Language Processing'});
db.courses.insert({'description': 'Introduces computational aspects of computer-aided design and manufacturing. Explores relevant methods in the context of additive manufacturing (e.g., 3D printing). Topics include computer graphics (geometry modeling, solid modeling, procedural modeling), physically-based simulation (kinematics, finite element method), 3D scanning/geometry processing, and an overview of 3D fabrication methods. Exposes students to the latest research in computational fabrication.', 'title': '6.807 Computational Fabrication', 'total_units': 12, 'course_numbers': ['6.807'], 'units': '3-0-9', 'name': 'Computational Fabrication'});
db.courses.insert({'name': 'Interactive Music Systems (New)', 'tags': ['HASS'], 'title': '6.809[J] Interactive Music Systems (New)', 'total_units': 12, 'course_numbers': ['6.809[J]'], 'units': '3-0-9', 'description': 'See description under subject 21M.385[J].'});
db.courses.insert({'description': 'Students work closely with people with disabilities to develop assistive and adaptive technologies that help them live more independently. Covers design methods and problem-solving strategies; human factors; human-machine interfaces; community perspectives; social and ethical aspects; and assistive technology for motor, cognitive, perceptual, and age-related impairments. Prior knowledge of one or more of the following areas useful: software; electronics; human-computer interaction; cognitive science; mechanical engineering; control; or MIT hobby shop, MIT PSC, or other relevant independent project experience.', 'title': '6.811[J] Principles and Practice of Assistive Technology', 'total_units': 12, 'course_numbers': ['6.811[J]'], 'units': '2-4-6', 'name': 'Principles and Practice of Assistive Technology'});
db.courses.insert({'description': 'Examines human-computer interaction in the context of graphical user interfaces. Covers human capabilities, design principles, prototyping techniques, evaluation techniques, and the implementation of graphical user interfaces. Includes short programming assignments and a semester-long group project. Students taking the graduate version also have readings from current literature and additional assignments.', 'title': '6.813 User Interface Design and Implementation', 'total_units': 12, 'course_numbers': ['6.813'], 'units': '4-0-8', 'name': 'User Interface Design and Implementation'});
db.courses.insert({'description': 'Topics related to the engineering and design of database systems, including data models; database and schema design; schema normalization and integrity constraints; query processing; query optimization and cost estimation; transactions; recovery; concurrency control; isolation and consistency; distributed, parallel and heterogeneous databases; adaptive databases; trigger systems; pub-sub systems; semi structured data and XML querying. Lecture and readings from original research papers. Semester-long project and paper. Students taking graduate version complete different assignments.', 'title': '6.814 Database Systems', 'total_units': 12, 'course_numbers': ['6.814'], 'units': '3-0-9', 'name': 'Database Systems'});
db.courses.insert({'description': 'Presents fundamentals and applications of hardware and software techniques used in digital and computational photography, with an emphasis on software methods. Provides sufficient background to implement solutions to photographic challenges and opportunities. Topics include cameras and image formation, image processing and image representations, high-dynamic-range imaging, human visual perception and color, single view 3-D model reconstruction, morphing, data-rich photography, super-resolution, and image-based rendering. Students taking graduate version complete additional assignments.', 'title': '6.815 Digital and Computational Photography', 'total_units': 12, 'course_numbers': ['6.815'], 'units': '3-0-9', 'name': 'Digital and Computational Photography'});
db.courses.insert({'description': 'Introduces principles and core techniques for programming multicore machines. Topics include locking, scalability, concurrent data structures, multiprocessor scheduling, load balancing, and state-of-the-art synchronization techniques, such as transactional memory. Includes sequence of programming assignments on a large multicore machine, culminating with the design of a highly concurrent application. Students taking graduate version complete additional assignments.', 'title': '6.816 Multicore Programming', 'total_units': 12, 'course_numbers': ['6.816'], 'units': '4-0-8', 'name': 'Multicore Programming'});
db.courses.insert({'description': 'Advanced topics in computer vision with a focus on the use of machine learning techniques and applications in graphics and human-computer interface. Covers image representations, texture models, structure-from-motion algorithms, Bayesian techniques, object and scene recognition, tracking, shape modeling, and image databases. Applications may include face recognition, multimodal interaction, interactive systems, cinematic special effects, and photorealistic rendering. Covers topics complementary to 6.801. Students taking graduate version complete additional assignments.', 'title': '6.819 Advances in Computer Vision', 'total_units': 12, 'course_numbers': ['6.819'], 'units': '3-0-9', 'name': 'Advances in Computer Vision'});
db.courses.insert({'description': 'Presents major principles and techniques for program analysis. Includes formal semantics, type systems and type-based program analysis, abstract interpretation and model checking and synthesis. Emphasis on Haskell and Ocaml, but no prior experience in these languages is assumed. Student assignments include implementing of techniques covered in class, including building simple verifiers.', 'title': '6.820 Foundations of Program Analysis', 'total_units': 12, 'course_numbers': ['6.820'], 'units': '3-0-9', 'name': 'Foundations of Program Analysis'});
db.courses.insert({'description': 'Introduction to the principles underlying modern computer architecture. Emphasizes the relationship among technology, hardware organization, and programming systems in the evolution of computer architecture. Topics include pipelined, out-of-order, and speculative execution; caches, virtual memory and exception handling, superscalar, very long instruction word (VLIW), vector, and multithreaded processors; on-chip networks, memory models, synchronization, and cache coherence protocols for multiprocessors.', 'title': '6.823 Computer System Architecture', 'total_units': 12, 'course_numbers': ['6.823'], 'units': '4-0-8', 'name': 'Computer System Architecture'});
db.courses.insert({'description': 'Abstractions and implementation techniques for engineering distributed systems: remote procedure call, threads and locking, client/server, peer-to-peer, consistency, fault tolerance, and security. Readings from current literature. Individual laboratory assignments culminate in the construction of a fault-tolerant and scalable network file system. Programming experience with C/C++ required.', 'title': '6.824 Distributed Computer Systems Engineering', 'total_units': 12, 'course_numbers': ['6.824'], 'units': '3-0-9', 'name': 'Distributed Computer Systems Engineering'});
db.courses.insert({'description': 'Fundamental design and implementation issues in the engineering of operating systems. Lectures based on the study of a symmetric multiprocessor version of UNIX version 6 and research papers. Topics include virtual memory; file system; threads; context switches; kernels; interrupts; system calls; interprocess communication; coordination, and interaction between software and hardware. Individual laboratory assignments accumulate in the construction of a minimal operating system (for an x86-based personal computer) that implements the basic operating system abstractions and a shell. Knowledge of programming in the C language is a prerequisite.', 'title': '6.828 Operating System Engineering', 'total_units': 12, 'course_numbers': ['6.828'], 'units': '3-6-3', 'name': 'Operating System Engineering'});
db.courses.insert({'description': 'Topics on the engineering and analysis of network protocols and architecture, including architectural principles for designing heterogeneous networks; transport protocols; Internet routing; router design; congestion control and network resource management; wireless networks; network security; naming; overlay and peer-to-peer networks. Readings from original research papers. Semester-long project and paper.', 'title': '6.829 Computer Networks', 'total_units': 12, 'course_numbers': ['6.829'], 'units': '4-0-8', 'name': 'Computer Networks'});
db.courses.insert({'description': 'Topics related to the engineering and design of database systems, including data models; database and schema design; schema normalization and integrity constraints; query processing; query optimization and cost estimation; transactions; recovery; concurrency control; isolation and consistency; distributed, parallel and heterogeneous databases; adaptive databases; trigger systems; pub-sub systems; semi structured data and XML querying. Lecture and readings from original research papers. Semester-long project and paper. Students taking graduate version complete different assignments.', 'title': '6.830 Database Systems', 'total_units': 12, 'course_numbers': ['6.830'], 'units': '3-0-9', 'name': 'Database Systems'});
db.courses.insert({'description': 'Examines human-computer interaction in the context of graphical user interfaces. Covers human capabilities, design principles, prototyping techniques, evaluation techniques, and the implementation of graphical user interfaces. Includes short programming assignments and a semester-long group project. Students taking the graduate version also have readings from current literature and additional assignments.', 'title': '6.831 User Interface Design and Implementation', 'total_units': 12, 'course_numbers': ['6.831'], 'units': '4-0-8', 'name': 'User Interface Design and Implementation'});
db.courses.insert({'description': 'Covers nonlinear dynamics and control of underactuated mechanical systems, with an emphasis on computational methods. Topics include nonlinear dynamics of passive robots (walkers, swimmers, flyers), motion planning, robust and optimal control, reinforcement learning/approximate optimal control, and the influence of mechanical design on control. Includes examples from biology and applications to legged locomotion, compliant manipulation, underwater robots, and flying machines.', 'title': '6.832 Underactuated Robotics', 'total_units': 12, 'course_numbers': ['6.832'], 'units': '3-0-9', 'name': 'Underactuated Robotics'});
db.courses.insert({'description': 'Analyzes seminal work directed at the development of a computational understanding of human intelligence, such as work on learning, language, vision, event representation, commonsense reasoning, self reflection, story understanding, and analogy. Reviews visionary ideas of Turing, Minsky, and other influential thinkers. Examines the implications of work on brain scanning, developmental psychology, and cognitive psychology. Emphasis on discussion and analysis of original papers. Requires the completion of additional exercises and a substantial term project.', 'title': '6.833 The Human Intelligence Enterprise', 'total_units': 12, 'course_numbers': ['6.833'], 'units': '3-0-9', 'name': 'The Human Intelligence Enterprise'});
db.courses.insert({'description': 'See description under subject 16.412[J].', 'title': '6.834[J] Cognitive Robotics', 'total_units': 12, 'course_numbers': ['6.834[J]'], 'units': '3-0-9', 'name': 'Cognitive Robotics'});
db.courses.insert({'description': 'Implementation and evaluation of intelligent multi-modal user interfaces, taught from a combination of hands-on exercises and papers from the original literature. Topics include basic technologies for handling speech, vision, pen-based interaction, and other modalities, as well as various techniques for combining modalities. Substantial readings and a term project, where students build an interface to illustrate one or more themes of the course.', 'title': '6.835 Intelligent Multimodal User Interfaces', 'total_units': 12, 'course_numbers': ['6.835'], 'units': '3-0-9', 'name': 'Intelligent Multimodal User Interfaces'});
db.courses.insert({'description': 'Introduces principles and core techniques for programming multicore machines. Topics include locking, scalability, concurrent data structures, multiprocessor scheduling, load balancing, and state-of-the-art synchronization techniques, such as transactional memory. Includes sequence of programming assignments on a large multicore machine, culminating with the design of a highly concurrent application. Students taking graduate version complete additional assignments.', 'title': '6.836 Multicore Programming', 'total_units': 12, 'course_numbers': ['6.836'], 'units': '4-0-8', 'name': 'Multicore Programming'});
db.courses.insert({'description': 'Introduction to computer graphics algorithms, software and hardware. Topics include ray tracing, the graphics pipeline, transformations, texture mapping, shadows, sampling, global illumination, splines, animation and color.', 'title': '6.837 Computer Graphics', 'total_units': 12, 'course_numbers': ['6.837'], 'units': '3-0-9', 'name': 'Computer Graphics'});
db.courses.insert({'description': 'In-depth study of an active research topic in computer graphics. Topics change each term. Readings from the literature, student presentations, short assignments, and a programming project.', 'title': '6.838 Advanced Topics in Computer Graphics', 'total_units': 12, 'course_numbers': ['6.838'], 'units': 'Can be repeated for credit.', 'name': 'Advanced Topics in Computer Graphics'});
db.courses.insert({'description': 'A graduate level course investigates computational problems in rendering, animation, and geometric modeling. The course draws on advanced techniques from computational geometry, applied mathematics, statistics, scientific computing and other. Substantial programming experience required.', 'title': '6.839 Advanced Computer Graphics', 'total_units': 12, 'course_numbers': ['6.839'], 'units': '3-0-9', 'name': 'Advanced Computer Graphics'});
db.courses.insert({'description': 'See description under subject 18.4041[J].', 'title': '6.840[J] Theory of Computation', 'total_units': 12, 'course_numbers': ['6.840[J]'], 'units': '4-0-8', 'name': 'Theory of Computation'});
db.courses.insert({'description': 'See description under subject 18.405[J].', 'title': '6.841[J] Advanced Complexity Theory', 'total_units': 12, 'course_numbers': ['6.841[J]'], 'units': '3-0-9', 'name': 'Advanced Complexity Theory'});
db.courses.insert({'description': 'The power and sources of randomness in computation. Connections and applications to computational complexity, computational learning theory, cryptography and combinatorics. Topics include: probabilistic proofs, uniform generation and approximate counting, Fourier analysis of Boolean functions, computational learning theory, expander graphs, pseudorandom generators, derandomization.', 'title': '6.842 Randomness and Computation', 'total_units': 12, 'course_numbers': ['6.842'], 'units': '3-0-9', 'name': 'Randomness and Computation'});
db.courses.insert({'description': 'Introduction to quantum computational complexity theory, the study of the fundamental capabilities and limitations of quantum computers. Topics include complexity classes, lower bounds, communication complexity, proofs and advice, and interactive proof systems in the quantum world; classical simulation of quantum circuits. The objective is to bring students to the research frontier.', 'title': '6.845 Quantum Complexity Theory', 'total_units': 12, 'course_numbers': ['6.845'], 'units': '3-0-9', 'name': 'Quantum Complexity Theory'});
db.courses.insert({'description': 'Introduction to parallel and multicore computer architecture and programming. Topics include the design and implementation of multicore processors; networking, video, continuum, particle and graph applications for multicores; communication and synchronization algorithms and mechanisms; locality in parallel computations; computational models, including shared memory, streams, message passing, and data parallel; multicore mechanisms for synchronization, cache coherence, and multithreading. Performance evaluation of multicores; compilation and runtime systems for parallel computing. Substantial project required.', 'title': '6.846 Parallel Computing', 'total_units': 12, 'course_numbers': ['6.846'], 'units': '3-0-9', 'name': 'Parallel Computing'});
db.courses.insert({'description': "Covers discrete geometry and algorithms underlying the reconfiguration of foldable structures, with applications to robotics, manufacturing, and biology. Linkages made from one-dimensional rods connected by hinges: constructing polynomial curves, characterizing rigidity, characterizing unfoldable versus locked, protein folding. Folding two-dimensional paper (origami): characterizing flat foldability, algorithmic origami design, one-cut magic trick. Unfolding and folding three-dimensional polyhedra: edge unfolding, vertex unfolding, gluings, Alexandrov's Theorem, hinged dissections.", 'title': '6.849 Geometric Folding Algorithms: Linkages, Origami, Polyhedra', 'total_units': 12, 'course_numbers': ['6.849'], 'units': '3-0-9', 'name': 'Geometric Folding Algorithms: Linkages, Origami, Polyhedra'});
db.courses.insert({'description': 'Introduction to the design and analysis of algorithms for geometric problems, in low- and high-dimensional spaces. Algorithms: convex hulls, polygon triangulation, Delaunay triangulation, motion planning, pattern matching. Geometric data structures: point location, Voronoi diagrams, Binary Space Partitions. Geometric problems in higher dimensions: linear programming, closest pair problems. High-dimensional nearest neighbor search and low-distortion embeddings between metric spaces. Geometric algorithms for massive data sets: external memory and streaming algorithms. Geometric optimization.', 'title': '6.850 Geometric Computing', 'total_units': 12, 'course_numbers': ['6.850'], 'units': '3-0-9', 'name': 'Geometric Computing'});
db.courses.insert({'description': 'More advanced and powerful data structures for answering several queries on the same data. Such structures are crucial in particular for designing efficient algorithms. Dictionaries; hashing; search trees. Self-adjusting data structures; linear search; splay trees; dynamic optimality. Integer data structures; word RAM. Predecessor problem; van Emde Boas priority queues; y-fast trees; fusion trees. Lower bounds; cell-probe model; round elimination. Dynamic graphs; link-cut trees; dynamic connectivity. Strings; text indexing; suffix arrays; suffix trees. Static data structures; compact arrays; rank and select. Succinct data structures; tree encodings; implicit data structures. External-memory and cache-oblivious data structures; B-trees; buffer trees; tree layout; ordered-file maintenance. Temporal data structures; persistence; retroactivity.', 'title': '6.851 Advanced Data Structures', 'total_units': 12, 'course_numbers': ['6.851'], 'units': '3-0-9', 'name': 'Advanced Data Structures'});
db.courses.insert({'description': 'Design and analysis of concurrent algorithms, emphasizing those suitable for use in distributed networks. Process synchronization, allocation of computational resources, distributed consensus, distributed graph algorithms, election of a leader in a network, distributed termination, deadlock detection, concurrency control, communication, and clock synchronization. Special consideration given to issues of efficiency and fault tolerance. Formal models and proof methods for distributed computation.', 'title': '6.852[J] Distributed Algorithms', 'total_units': 12, 'course_numbers': ['6.852[J]'], 'units': '3-0-9', 'name': 'Distributed Algorithms'});
db.courses.insert({'description': 'Presents research topics at the interface of computer science and game theory, with an emphasis on algorithms and computational complexity. Explores the types of game-theoretic tools that are applicable to computer systems, the loss in system performance due to the conflicts of interest of users and administrators, and the design of systems whose performance is robust with respect to conflicts of interest inside the system. Algorithmic focus is on algorithms for equilibria, the complexity of equilibria and fixed points, algorithmic tools in mechanism design, learning in games, and the price of anarchy.', 'title': '6.853 Topics in Algorithmic Game Theory', 'total_units': 12, 'course_numbers': ['6.853'], 'units': '3-0-9', 'name': 'Topics in Algorithmic Game Theory'});
db.courses.insert({'description': 'First-year graduate subject in algorithms. Emphasizes fundamental algorithms and advanced methods of algorithmic design, analysis, and implementation. Surveys a variety of computational models and the algorithms for them. Data structures, network flows, linear programming, computational geometry, approximation algorithms, online algorithms, parallel algorithms, external memory, streaming algorithms.', 'title': '6.854[J] Advanced Algorithms', 'total_units': 12, 'course_numbers': ['6.854[J]'], 'units': '5-0-7', 'name': 'Advanced Algorithms'});
db.courses.insert({'description': 'Studies how randomization can be used to make algorithms simpler and more efficient via random sampling, random selection of witnesses, symmetry breaking, and Markov chains. Models of randomized computation. Data structures: hash tables, and skip lists. Graph algorithms: minimum spanning trees, shortest paths, and minimum cuts. Geometric algorithms: convex hulls, linear programming in fixed or arbitrary dimension. Approximate counting; parallel algorithms; online algorithms; derandomization techniques; and tools for probabilistic analysis of algorithms.', 'title': '6.856[J] Randomized Algorithms', 'total_units': 12, 'course_numbers': ['6.856[J]'], 'units': '5-0-7', 'name': 'Randomized Algorithms'});
db.courses.insert({'description': 'Emphasis on applied cryptography and may include: basic notion of systems security, crypotographic hash functions, symmetric crypotography (one-time pad, stream ciphers, block ciphers), cryptanalysis, secret-sharing, authentication codes, public-key cryptography (encryption, digital signatures), public-key attacks, web browser security, biometrics, electronic cash, viruses, electronic voting, Assignments include a group final project. Topics may vary year to year.', 'title': '6.857 Network and Computer Security', 'total_units': 12, 'course_numbers': ['6.857'], 'units': '4-0-8', 'name': 'Network and Computer Security'});
db.courses.insert({'description': 'Design and implementation of secure computer systems. Lectures cover attacks that compromise security as well as techniques for achieving security, based on recent research papers. Topics include operating system security, privilege separation, capabilities, language-based security, cryptographic network protocols, trusted hardware, and security in web applications and mobile phones. Labs involve implementing and compromising a web application that sandboxes arbitrary code, and a group final project.', 'title': '6.858 Computer Systems Security', 'total_units': 12, 'course_numbers': ['6.858'], 'units': '3-6-3', 'name': 'Computer Systems Security'});
db.courses.insert({'description': 'See description under subject 9.520[J].', 'title': '6.860[J] Statistical Learning Theory and Applications (New)', 'total_units': 12, 'course_numbers': ['6.860[J]'], 'units': '3-0-9', 'name': 'Statistical Learning Theory and Applications (New)'});
db.courses.insert({'description': 'See description under subject 9.523[J].', 'title': '6.861[J] Aspects of a Computational Theory of Intelligence (New)', 'total_units': 12, 'course_numbers': ['6.861[J]'], 'units': '3-0-9', 'name': 'Aspects of a Computational Theory of Intelligence (New)'});
db.courses.insert({'description': 'Introduces principles, algorithms, and applications of machine learning from the point of view of modeling and prediction; formulation of learning problems; representation, over-fitting, generalization; clustering, classification, probabilistic modeling; and methods such as support vector machines, hidden Markov models, and Bayesian networks. Students taking graduate version complete different assignments.', 'title': '6.862 Applied Machine Learning (New)', 'total_units': 12, 'course_numbers': ['6.862'], 'units': '4-0-8', 'name': 'Applied Machine Learning (New)'});
db.courses.insert({'description': 'Explores the relationship between computer representation of knowledge and the structure of natural language. Emphasizes development of analytical skills necessary to judge the computational implications of grammatical formalisms, and uses concrete examples to illustrate particular computational issues. Efficient parsing algorithms for context-free grammars; Treebank grammars and statistical parsing. Question answering systems. Extensive laboratory work on building natural language processing systems. 8 Engineering Design Points.', 'title': '6.863[J] Natural Language and the Computer Representation of Knowledge', 'total_units': 12, 'course_numbers': ['6.863[J]'], 'units': '3-3-6', 'name': 'Natural Language and the Computer Representation of Knowledge'});
db.courses.insert({'description': 'Introduces the study of human language from a computational perspective, including syntactic, semantic and discourse processing models. Emphasizes machine learning methods and algorithms. Uses these methods and models in applications such as syntactic parsing, information extraction, statistical machine translation, dialogue systems, and summarization. Students taking graduate version complete additional assignments.', 'title': '6.864 Advanced Natural Language Processing', 'total_units': 12, 'course_numbers': ['6.864'], 'units': '3-0-9', 'name': 'Advanced Natural Language Processing'});
db.courses.insert({'description': 'Presents fundamentals and applications of hardware and software techniques used in digital and computational photography, with an emphasis on software methods. Provides sufficient background to implement solutions to photographic challenges and opportunities. Topics include cameras and image formation, image processing and image representations, high-dynamic-range imaging, human visual perception and color, single view 3-D model reconstruction, morphing, data-rich photography, super-resolution, and image-based rendering. Students taking graduate version complete additional assignments.', 'title': '6.865 Advanced Computational Photography', 'total_units': 12, 'course_numbers': ['6.865'], 'units': '3-0-9', 'name': 'Advanced Computational Photography'});
db.courses.insert({'description': 'Intensive introduction to the process of generating a symbolic description of the environment from an image. Students expected to attend the 6.801 lectures as well as occasional seminar meetings on special topics. Material presented in 6.801 is supplemented by reading from the literature. Students required to implement a project on a topic of their choice from the material covered.', 'title': '6.866 Machine Vision', 'total_units': 12, 'course_numbers': ['6.866'], 'units': '3-0-9', 'name': 'Machine Vision'});
db.courses.insert({'description': 'Principles, techniques, and algorithms in machine learning from the point of view of statistical inference; representation, generalization, and model selection; and methods such as linear/additive models, active learning, boosting, support vector machines, non-parametric Bayesian methods, hidden Markov models, and Bayesian networks. Recommended prerequisite: 6.036.', 'title': '6.867 Machine Learning', 'total_units': 12, 'course_numbers': ['6.867'], 'units': '3-0-9', 'name': 'Machine Learning'});
db.courses.insert({'name': 'The Society of Mind', 'tags': [''], 'title': '6.868[J] The Society of Mind', 'total_units': 3, 'course_numbers': ['6.868[J]'], 'units': '2-0-1', 'description': 'Introduction to a theory that tries to explain how minds are made from collections of simpler processes. Treats such aspects of thinking as vision, language, learning, reasoning, memory, consciousness, ideals, emotions, and personality. Incorporates ideas from psychology, artificial intelligence, and computer science to resolve theoretical issues such as wholes vs. parts, structural vs. functional descriptions, declarative vs. procedural representations, symbolic vs. connectionist models, and logical vs. common-sense theories of learning.'});
db.courses.insert({'description': 'Advanced topics in computer vision with a focus on the use of machine learning techniques and applications in graphics and human-computer interface. Covers image representations, texture models, structure-from-motion algorithms, Bayesian techniques, object and scene recognition, tracking, shape modeling, and image databases. Applications may include face recognition, multimodal interaction, interactive systems, cinematic special effects, and photorealistic rendering. Covers topics complementary to 6.866. Students taking graduate version complete additional assignments.', 'title': '6.869 Advances in Computer Vision', 'total_units': 12, 'course_numbers': ['6.869'], 'units': '3-0-9', 'name': 'Advances in Computer Vision'});
db.courses.insert({'description': 'Seminar exploring advanced research topics in the field of computer vision; focus varies with lecturer. Typically structured around discussion of assigned research papers and presentations by students. Example research areas explored in this seminar include learning in vision, computational imaging techniques, multimodal human-computer interaction, biomedical imaging, representation and estimation methods used in modern computer vision.', 'title': '6.870 Advanced Topics in Computer Vision', 'total_units': 12, 'course_numbers': ['6.870'], 'units': 'Can be repeated for credit.', 'name': 'Advanced Topics in Computer Vision'});
db.courses.insert({'description': 'Project-based introduction to building efficient, high-performance and scalable software systems. Topics include performance analysis, algorithmic techniques for high performance, instruction-level optimizations, vectorization, cache and memory hierarchy optimization, and parallel programming. Students taking graduate version complete additional assignments.', 'title': '6.871 Performance Engineering of Software Systems', 'total_units': 18, 'course_numbers': ['6.871'], 'units': '3-12-', 'name': 'Performance Engineering of Software Systems'});
db.courses.insert({'description': 'Analyzes computational needs of clinical medicine, reviews systems and approaches that have been used to support those needs, and the relationship between clinical data and gene and protein measurements to support precision medicine. Topics include the nature of clinical data, architecture and design of healthcare information systems, privacy and security issues, medical expert systems, predictive models and machine learning from big data in healthcare, and an introduction to bioinformatics. Case studies and guest lectures describe contemporary institutions, systems, and research projects. Term project using large clinical and genomic data sets integrates classroom topics.', 'title': '6.872[J] Biomedical Computing', 'total_units': 12, 'course_numbers': ['6.872[J]'], 'units': '3-0-9', 'name': 'Biomedical Computing'});
db.courses.insert({'description': 'Presents advanced machine learning and algorithmic approaches for contemporary problems in biology drawing upon recent advances in the literature. Topics include biological discovery in heterogeneous cellular populations; single cell data analysis; regulatory factor binding; motif discovery; gene expression analysis; regulatory networks (discovery, validation, data integration, protein-protein interactions, signaling, chromatin accessibility analysis); predicting phenotype from genotype; and experimental design (model validation, interpretation of interventions). Computational methods presented include deep learning, dimensionality reduction, clustering, directed and undirected graphical models, significance testing, Dirichlet processes, and topic models. Multidisciplinary team-oriented final research project.', 'title': '6.874[J] Computational Systems Biology', 'total_units': 12, 'course_numbers': ['6.874[J]'], 'units': '3-0-9', 'name': 'Computational Systems Biology'});
db.courses.insert({'description': 'A rigorous introduction to modern cryptography. Emphasis on the fundamental cryptographic primitives of public-key encryption, digital signatures, pseudo-random number generation, and basic protocols and their computational complexity requirements.', 'title': '6.875[J] Cryptography and Cryptanalysis', 'total_units': 12, 'course_numbers': ['6.875[J]'], 'units': '3-0-9', 'name': 'Cryptography and Cryptanalysis'});
db.courses.insert({'description': 'Recent results in cryptography, interactive proofs, and cryptographic game theory.', 'title': '6.876 Advanced Topics in Cryptography', 'total_units': 12, 'course_numbers': ['6.876'], 'units': 'Can be repeated for credit.', 'name': 'Advanced Topics in Cryptography'});
db.courses.insert({'description': 'See description for 6.047. Additionally examines recent publications in the areas covered, with research-style assignments. A more substantial final project is expected, which can lead to a thesis and publication.', 'title': '6.878[J] Advanced Computational Biology: Genomes, Networks, Evolution', 'total_units': 12, 'course_numbers': ['6.878[J]'], 'units': '4-0-8', 'name': 'Advanced Computational Biology: Genomes, Networks, Evolution'});
