db.courses.insert({'units': '0-12-0 units', 'name': 'Graduate 6-A Internship', 'course_numbers': ['6.951'], 'description': "Provides academic credit for a graduate assignment of graduate 6-A students at companies affiliated with the department's 6-A internship program."});
db.courses.insert({'units': '0-12-0 units', 'name': 'Graduate 6-A Internship', 'course_numbers': ['6.952'], 'description': "Provides academic credit for graduate students who require an additional term at the company to complete the graduate assignment of the department's 6-A internship program. This academic credit is for registration purposes only and cannot be used toward fulfilling the requirements of any degree program."});
db.courses.insert({'units': 'Units arranged [P/D/F]Can be repeated for credit.', 'name': 'Introductory Research in Electrical Engineering and Computer Science', 'course_numbers': ['6.960'], 'description': 'Enrollment restricted to first-year graduate students in Electrical Engineering and Computer Science who are doing introductory research leading to an SM, EE, ECS, PhD, or ScD thesis. Opportunity to become involved in graduate research, under guidance of a staff member, on a problem of mutual interest to student and supervisor. Individual programs subject to approval of professor in charge.'});
